import React, { useState, useEffect } from "react";
import axios from "axios";

const Profile = () => {
  const [items, setItems] = useState([]);
  const [user, setUser] = useState(null); // Информация о пользователе

  // Получение данных пользователя и созданных им элементов
  useEffect(() => {
    const fetchData = async () => {
      try {
        const username = localStorage.getItem("username");
        setUser(username);

        const response = await axios.get("http://localhost:5000/api/data");
        const userItems = response.data.filter(
          (item) => item.createdBy === username
        );
        setItems(userItems);
      } catch (error) {
        console.error("Ошибка при загрузке данных:", error);
      }
    };

    fetchData();
  }, []);

  if (!user) {
    return <p>Загрузка...</p>;
  }

  return (
    <div>
      <h1>Профиль пользователя: {user}</h1>
      <p>Количество созданных элементов: {items.length}</p>
      <p>Общее количество сумм заказов {item.price}</p>

      <h2>Все созданные элементы:</h2>
      <ul>
        {items.map((item) => (
          <li key={item._id}>
            <p>
              <strong>Дата:</strong> {new Date(item.date).toLocaleDateString()}
            </p>
            <p>
              <strong>Бренд:</strong> {item.brand}
            </p>
            <p>
              <strong>Водитель:</strong> {item.driver}
            </p>
            <p>
              <strong>Клиент:</strong> {item.customer}
            </p>
            <p>
              <strong>Номер маршрута:</strong> {item.routeNumber}
            </p>
            <p>
              <strong>Часы:</strong> {item.hours || "Не указано"}
            </p>
            <p>
              <strong>Цена:</strong> {item.price}
            </p>
            <p>
              <strong>Комментарий:</strong> {item.comment}
            </p>
            <p>
              <strong>Создано:</strong>{" "}
              {new Date(item.createdAt).toLocaleString()}
            </p>
            <p>
              <strong>Обновлено:</strong>{" "}
              {new Date(item.updatedAt).toLocaleString()}
            </p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Profile;
