import React, { useState, useEffect } from "react";
import axios from "axios";
import "./styles.css";
const Home = () => {
  const [data, setData] = useState([]);
  const [formVisible, setFormVisible] = useState(false);
  const [newItem, setNewItem] = useState({
    date: "",
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isActive: true,
    licensePlate: "",
    price: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewItem((prevItem) => ({
      ...prevItem,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formattedDate = new Date(newItem.date).toISOString(); // Форматирование даты
      const response = await axios.post("http://localhost:5000/api/data", {
        ...newItem,
        date: formattedDate,
      });
      setData([...data, response.data]);
      setFormVisible(false);
      setNewItem({
        date: "",
        brand: "",
        driver: "",
        customer: "",
        routeNumber: "",
        hours: "",
        isActive: true,
        licensePlate: "",
        price: "",
      });
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  return (
    <div>
      <h1>Data Table</h1>
      <button onClick={() => setFormVisible(!formVisible)}>
        {formVisible ? "Cancel" : "Add New Item"}
      </button>

      {formVisible && (
        <form onSubmit={handleSubmit}>
          <input
            type="date"
            name="date"
            value={newItem.date}
            onChange={handleChange}
          />
          <input
            type="text"
            name="brand"
            value={newItem.brand}
            onChange={handleChange}
            placeholder="Марка"
          />
          <input
            type="text"
            name="driver"
            value={newItem.driver}
            onChange={handleChange}
            placeholder="ВОДИТЕЛЬ"
          />
          <input
            type="text"
            name="customer"
            value={newItem.customer}
            onChange={handleChange}
            placeholder="ЗАКАЗЧИК"
          />
          <input
            type="text"
            name="routeNumber"
            value={newItem.routeNumber}
            onChange={handleChange}
            placeholder="№ пут"
          />
          <input
            type="number"
            name="hours"
            value={newItem.hours}
            onChange={handleChange}
            placeholder="ЧАСЫ"
          />
          <select
            name="isActive"
            value={newItem.isActive}
            onChange={handleChange}
          >
            <option value={true}>TRUE</option>
            <option value={false}>FALSE</option>
          </select>
          <input
            type="text"
            name="licensePlate"
            value={newItem.licensePlate}
            onChange={handleChange}
            placeholder="Гос №"
          />
          <input
            type="number"
            name="price"
            value={newItem.price}
            onChange={handleChange}
            placeholder="ЦЕНА"
          />
          <button type="submit">Add Data</button>
        </form>
      )}

      <table>
        <thead>
          <tr>
            <th>Дата</th>
            <th>Марка</th>
            <th>ВОДИТЕЛЬ</th>
            <th>ЗАКАЗЧИК</th>
            <th>№ пут</th>
            <th>ЧАСЫ</th>
            <th>FALSE/true</th>
            <th>Гос №</th>
            <th>ЦЕНА</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td>{new Date(item.date).toLocaleDateString()}</td>
              <td>{item.brand}</td>
              <td>{item.driver}</td>
              <td>{item.customer}</td>
              <td>{item.routeNumber}</td>
              <td>{item.hours}</td>
              <td>{item.isActive ? "TRUE" : "FALSE"}</td>
              <td>{item.licensePlate}</td>
              <td>{item.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
