import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import dayjs from "dayjs";

const Profile = () => {
  const [items, setItems] = useState([]);
  const [user, setUser] = useState(null);
  const [drivers, setDrivers] = useState([]); // Список водителей
  const [selectedDriver, setSelectedDriver] = useState(""); // Выбранный водитель
  const apiUrl = "https://bam-app-489c6c1370a9.herokuapp.com";

  useEffect(() => {
    const fetchData = async () => {
      try {
        const username = localStorage.getItem("username");
        setUser(username);

        const response = await axios.get(`${apiUrl}/api/data`);
        const userItems = response.data.filter(
          (item) => item.user === username
        );
        setItems(userItems);

        // Получаем список уникальных водителей
        const uniqueDrivers = [
          ...new Set(
            userItems
              .map((item) => item.driver)
              .filter(
                (driver) => typeof driver === "string" && driver.trim() !== ""
              )
          ),
        ];
        setDrivers(uniqueDrivers);
      } catch (error) {
        console.error("Ошибка при загрузке данных:", error);
      }
    };

    fetchData();
  }, []);

  if (!user) {
    return <p>Загрузка...</p>;
  }

  // Фильтруем заявки по выбранному водителю
  const filteredItems = items.filter(
    (item) =>
      (selectedDriver === "" || item.driver === selectedDriver) &&
      item.colorClass !== ""
  );

  const totalPrice = filteredItems.reduce((total, item) => {
    const price = parseFloat(item.price) || 0;
    return total + price;
  }, 0);

  const totalCalcPay = filteredItems.reduce((total, item) => {
    const calcPay = parseFloat(item.calcPay) || 0;
    return total + calcPay;
  }, 0);

  return (
    <div>
      <Link to="/">
        <button>Go to Home</button>
      </Link>
      <h1>Профиль пользователя: {user}</h1>

      {/* Инпут для выбора водителя */}
      <div>
        <label htmlFor="driver">Выберите водителя:</label>
        <select
          id="driver"
          value={selectedDriver}
          onChange={(e) => setSelectedDriver(e.target.value)}
        >
          <option value="">Все водители</option>
          {drivers.map((driver, index) => (
            <option key={index} value={driver}>
              {driver}
            </option>
          ))}
        </select>
      </div>

      <p>Количество созданных элементов: {filteredItems.length}</p>
      <p>Общее количество сумм заказов: {totalPrice}</p>
      <p>Общее значение calcPay: {totalCalcPay}</p>

      {/* Отображаем отфильтрованные элементы */}
      <div>
        <h2>Заявки выбранного водителя:</h2>
        {filteredItems.length > 0 ? (
          <ul>
            {filteredItems.map((item) => (
              <li key={item._id}>
                {item.brand || "Без имени"} - {item.price || "Цена не указана"}{" "}
                - {item.calcPay || "Без calcPay"} -{" "}
                {item.driver || "Без водителя"} -{" "}
                {item.vehicleNumber || "Без номера автомобиля"} -{" "}
                {dayjs(item.date).format("YYYY-MM-DD") || "Без даты"}
              </li>
            ))}
          </ul>
        ) : (
          <p>Нет заявок для отображения.</p>
        )}
      </div>
    </div>
  );
};

export default Profile;
