import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = () => {
  const [data, setData] = useState([]);
  const [editItem, setEditItem] = useState(null);
  const [form, setForm] = useState({
    date: "",
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  const handleEdit = (item) => {
    setEditItem(item);
    setForm({
      date: item.date || "",
      brand: item.brand || "",
      driver: item.driver || "",
      customer: item.customer || "",
      routeNumber: item.routeNumber || "",
      hours: item.hours || "",
      isTrue: item.isTrue || false,
      vehicleNumber: item.vehicleNumber || "",
      price: item.price || "",
    });
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({
      ...form,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/api/data/${editItem._id}`, form);
      const updatedData = data.map((item) =>
        item._id === editItem._id ? { ...item, ...form } : item
      );
      setData(updatedData);
      setEditItem(null);
    } catch (error) {
      console.error("Error saving data:", error);
    }
  };

  return (
    <div>
      <h1>Data Table</h1>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Brand</th>
            <th>Driver</th>
            <th>Customer</th>
            <th>Route Number</th>
            <th>Hours</th>
            <th>True/False</th>
            <th>Vehicle Number</th>
            <th>Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td>{item.date}</td>
              <td>{item.brand}</td>
              <td>{item.driver}</td>
              <td>{item.customer}</td>
              <td>{item.routeNumber}</td>
              <td>{item.hours}</td>
              <td>{item.isTrue ? "TRUE" : "FALSE"}</td>
              <td>{item.vehicleNumber}</td>
              <td>{item.price}</td>
              <td>
                <button onClick={() => handleEdit(item)}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editItem && (
        <div>
          <h2>Edit Item</h2>
          <form>
            <label>
              Date:
              <input
                type="text"
                name="date"
                value={form.date}
                onChange={handleChange}
              />
            </label>
            <label>
              Brand:
              <input
                type="text"
                name="brand"
                value={form.brand}
                onChange={handleChange}
              />
            </label>
            <label>
              Driver:
              <input
                type="text"
                name="driver"
                value={form.driver}
                onChange={handleChange}
              />
            </label>
            <label>
              Customer:
              <input
                type="text"
                name="customer"
                value={form.customer}
                onChange={handleChange}
              />
            </label>
            <label>
              Route Number:
              <input
                type="text"
                name="routeNumber"
                value={form.routeNumber}
                onChange={handleChange}
              />
            </label>
            <label>
              Hours:
              <input
                type="text"
                name="hours"
                value={form.hours}
                onChange={handleChange}
              />
            </label>
            <label>
              True/False:
              <input
                type="checkbox"
                name="isTrue"
                checked={form.isTrue}
                onChange={handleChange}
              />
            </label>
            <label>
              Vehicle Number:
              <input
                type="text"
                name="vehicleNumber"
                value={form.vehicleNumber}
                onChange={handleChange}
              />
            </label>
            <label>
              Price:
              <input
                type="text"
                name="price"
                value={form.price}
                onChange={handleChange}
              />
            </label>
            <button type="button" onClick={handleSave}>
              Save
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Home;
