import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split("T")[0],
    brand: "",
    driver: "", // Используйте ID водителя или текстовое значение
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
  });
  const [inputDriver, setInputDriver] = useState(""); // Для ручного ввода водителя
  const [selectedDriverId, setSelectedDriverId] = useState(""); // Для выбранного водителя из списка

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/drivers");
        setDrivers(response.data);
      } catch (error) {
        console.error("Error fetching drivers:", error);
      }
    };

    fetchData();
    fetchDrivers();
  }, []);

  const handleInputChange = (e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;

    setData(
      data.map((item) =>
        item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
      )
    );

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...data.find((item) => item._id === itemId),
        [fieldName]: updatedValue,
      })
      .catch((error) => console.error("Error saving data:", error));
  };

  const handleDriverChange = (e, itemId) => {
    const selectedDriver = e.target.value;

    if (selectedDriver === "new") {
      setInputDriver("");
      setSelectedDriverId(""); // Сбрасываем выбранного водителя, чтобы вводить новое имя
    } else {
      setInputDriver(""); // Сбрасываем ввод, если выбран существующий водитель
      setSelectedDriverId(selectedDriver);
      setData(
        data.map((item) =>
          item._id === itemId ? { ...item, driver: selectedDriver } : item
        )
      );

      axios
        .put(`http://localhost:5000/api/data/${itemId}`, {
          ...data.find((item) => item._id === itemId),
          driver: selectedDriver,
        })
        .catch((error) => console.error("Error saving data:", error));
    }
  };

  const handleManualDriverInput = (e, itemId) => {
    const value = e.target.value;
    setInputDriver(value);

    // Если не выбран ID водителя, используем текстовое значение
    if (selectedDriverId === "") {
      setData(
        data.map((item) =>
          item._id === itemId ? { ...item, driver: value } : item
        )
      );

      axios
        .put(`http://localhost:5000/api/data/${itemId}`, {
          ...data.find((item) => item._id === itemId),
          driver: value,
        })
        .catch((error) => console.error("Error saving data:", error));
    }
  };

  const addNewItem = async () => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/data",
        newItem
      );
      setData([...data, response.data]);
      // Reset new item fields after adding
      setNewItem({
        date: new Date().toISOString().split("T")[0],
        brand: "",
        driver: "",
        customer: "",
        routeNumber: "",
        hours: "",
        isTrue: false,
        vehicleNumber: "",
        price: "",
      });
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  return (
    <div>
      <h1>Data Table</h1>
      <button onClick={addNewItem}>Add New Item</button>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Brand</th>
            <th>Driver</th>
            <th>Customer</th>
            <th>Route Number</th>
            <th>Hours</th>
            <th>True/False</th>
            <th>Vehicle Number</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td>{new Date(item.date).toLocaleDateString()}</td>
              <td>
                <input
                  type="text"
                  value={item.brand || ""}
                  onChange={(e) => handleInputChange(e, item._id, "brand")}
                />
              </td>
              <td>
                <select
                  value={selectedDriverId || item.driver || "new"}
                  onChange={(e) => handleDriverChange(e, item._id)}
                >
                  <option value="new">Add New Driver</option>
                  {drivers.map((driver) => (
                    <option key={driver._id} value={driver._id}>
                      {driver.name}
                    </option>
                  ))}
                </select>
                {inputDriver && (
                  <input
                    type="text"
                    value={inputDriver}
                    onChange={(e) => handleManualDriverInput(e, item._id)}
                    placeholder="Enter new driver name"
                  />
                )}
              </td>
              <td>
                <input
                  type="text"
                  value={item.customer || ""}
                  onChange={(e) => handleInputChange(e, item._id, "customer")}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.routeNumber || ""}
                  onChange={(e) =>
                    handleInputChange(e, item._id, "routeNumber")
                  }
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.hours || ""}
                  onChange={(e) => handleInputChange(e, item._id, "hours")}
                />
              </td>
              <td>
                <input
                  type="checkbox"
                  checked={item.isTrue || false}
                  onChange={(e) => handleInputChange(e, item._id, "isTrue")}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.vehicleNumber || ""}
                  onChange={(e) =>
                    handleInputChange(e, item._id, "vehicleNumber")
                  }
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.price || ""}
                  onChange={(e) => handleInputChange(e, item._id, "price")}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
