// Home.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { hardcodedData } from "./hardcodedData";

const Home = () => {
  const [data, setData] = useState([]);
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().split("T")[0]
  );
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split("T")[0],
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
    isHighlighted: false,
  });
  const [highlightColor, setHighlightColor] = useState("transparent");

  // Получение данных с сервера
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  // Добавление данных для выбранной даты
  const addEntriesForSelectedDate = async () => {
    try {
      const newEntries = hardcodedData.map((item) => ({
        ...item,
        date: selectedDate,
        isHighlighted: highlightColor !== "transparent",
      }));

      await Promise.all(
        newEntries.map((entry) =>
          axios.post("http://localhost:5000/api/data", entry)
        )
      );

      setData([...data, ...newEntries]);
    } catch (error) {
      console.error("Error adding entries:", error);
    }
  };

  // Добавление нового элемента с цветом фона
  const addNewItemWithColor = async (color) => {
    try {
      const itemToAdd = { ...newItem, isHighlighted: color !== "transparent" };
      const response = await axios.post(
        "http://localhost:5000/api/data",
        itemToAdd
      );
      setData([...data, response.data]);
      setNewItem({
        date: new Date().toISOString().split("T")[0],
        brand: "",
        driver: "",
        customer: "",
        routeNumber: "",
        hours: "",
        isTrue: false,
        vehicleNumber: "",
        price: "",
        isHighlighted: false,
      });
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  // Функция для группировки данных по месяцам и дням
  const groupByMonthAndDay = (data) => {
    return data.reduce((groups, item) => {
      const date = new Date(item.date);
      const month = date.toLocaleString("default", { month: "long" });
      const day = date.getDate();

      if (!groups[month]) {
        groups[month] = {};
      }

      if (!groups[month][day]) {
        groups[month][day] = [];
      }

      groups[month][day].push(item);
      return groups;
    }, {});
  };

  const groupedData = groupByMonthAndDay(data);

  return (
    <div>
      <h1>Data Table</h1>
      <button
        onClick={() => {
          setHighlightColor("yellow");
          addEntriesForSelectedDate();
        }}
      >
        Add 37 Entries for Selected Date (Yellow)
      </button>
      <button
        onClick={() => {
          setHighlightColor("lightblue");
          addEntriesForSelectedDate();
        }}
      >
        Add 37 Entries for Selected Date (Light Blue)
      </button>
      <button onClick={() => addNewItemWithColor("transparent")}>
        Add New Item
      </button>
      <button
        onClick={() => addNewItemWithColor("yellow")}
        style={{ backgroundColor: "yellow" }}
      >
        Add Highlighted Item
      </button>

      <input
        type="date"
        value={selectedDate}
        onChange={(e) => setSelectedDate(e.target.value)}
      />

      {Object.entries(groupedData).map(([month, days]) => (
        <div key={month}>
          <h2>{month}</h2>
          {Object.entries(days).map(([day, items]) => (
            <div key={day}>
              <h3>{day}</h3>
              <table>
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Brand</th>
                    <th>Driver</th>
                    <th>Customer</th>
                    <th>Route Number</th>
                    <th>Hours</th>
                    <th>True/False</th>
                    <th>Vehicle Number</th>
                    <th>Price</th>
                  </tr>
                </thead>
                <tbody>
                  {items.map((item) => (
                    <tr
                      key={item._id}
                      style={{
                        backgroundColor: item.isHighlighted
                          ? "#ffff99"
                          : "transparent",
                      }}
                    >
                      <td>{item.date}</td>
                      <td>{item.brand}</td>
                      <td>{item.driver}</td>
                      <td>{item.customer}</td>
                      <td>{item.routeNumber}</td>
                      <td>{item.hours}</td>
                      <td>{item.isTrue ? "True" : "False"}</td>
                      <td>{item.vehicleNumber}</td>
                      <td>{item.price}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Home;
