                    <td>
                      <button
                        className="btn-del"
                        onClick={() => handleDeleteWithConfirmation(item._id)}
                        onMouseEnter={() => setHighlightId(item._id)}
                        onMouseLeave={() => setHighlightId(null)}
                        onTouchStart={() => setHighlightId(item._id)}
                      >
