import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = () => {
  const [data, setData] = useState([]);
  const [newData, setNewData] = useState({
    date: "",
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    status: false,
    licensePlate: "",
    price: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/data", newData);
      setNewData({
        date: "",
        brand: "",
        driver: "",
        customer: "",
        routeNumber: "",
        hours: "",
        status: false,
        licensePlate: "",
        price: "",
      });
      const response = await axios.get("http://localhost:5000/api/data");
      setData(response.data);
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  const formatDate = (date) => {
    const d = new Date(date);
    if (isNaN(d.getTime())) return "Invalid Date"; // Возвращаем строку для неверных дат
    return d.toLocaleDateString(); // Возвращаем дату в локальном формате
  };

  return (
    <div>
      <h1>Data Table</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="date"
          name="date"
          value={newData.date}
          onChange={handleInputChange}
          placeholder="Дата"
          required
        />
        <input
          type="text"
          name="brand"
          value={newData.brand}
          onChange={handleInputChange}
          placeholder="Марка"
          required
        />
        <input
          type="text"
          name="driver"
          value={newData.driver}
          onChange={handleInputChange}
          placeholder="ВОДИТЕЛЬ"
          required
        />
        <input
          type="text"
          name="customer"
          value={newData.customer}
          onChange={handleInputChange}
          placeholder="ЗАКАЗЧИК"
          required
        />
        <input
          type="text"
          name="routeNumber"
          value={newData.routeNumber}
          onChange={handleInputChange}
          placeholder="№ пут"
          required
        />
        <input
          type="number"
          name="hours"
          value={newData.hours}
          onChange={handleInputChange}
          placeholder="ЧАСЫ"
          required
        />
        <select
          name="status"
          value={newData.status}
          onChange={handleInputChange}
          required
        >
          <option value={false}>FALSE</option>
          <option value={true}>TRUE</option>
        </select>
        <input
          type="text"
          name="licensePlate"
          value={newData.licensePlate}
          onChange={handleInputChange}
          placeholder="Гос №"
          required
        />
        <input
          type="number"
          name="price"
          value={newData.price}
          onChange={handleInputChange}
          placeholder="ЦЕНА"
          required
        />
        <button type="submit">Add Data</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>Дата</th>
            <th>Марка</th>
            <th>ВОДИТЕЛЬ</th>
            <th>ЗАКАЗЧИК</th>
            <th>№ пут</th>
            <th>ЧАСЫ</th>
            <th>FALSE/true</th>
            <th>Гос №</th>
            <th>ЦЕНА</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td>{formatDate(item.date)}</td>
              <td>{item.brand}</td>
              <td>{item.driver}</td>
              <td>{item.customer}</td>
              <td>{item.routeNumber}</td>
              <td>{item.hours}</td>
              <td>{item.status ? "TRUE" : "FALSE"}</td>
              <td>{item.licensePlate}</td>
              <td>{item.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
