import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const MonthlyData = () => {
  const { month, year } = useParams();
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const filterDataByMonthAndYear = (data, month, year) => {
    return data.filter((item) => {
      const date = new Date(item.date);
      return (
        date.getMonth() === parseInt(month, 10) &&
        date.getFullYear() === parseInt(year, 10)
      );
    });
  };

  return (
    <div>
      <h1>
        Данные за {month} {year}
      </h1>
      <table>
        <thead>
          <tr>
            <th>Дата</th>
            <th>Бренд</th>
            <th>Водитель</th>
            <th>Заказчик</th>
            <th>Номер маршрута</th>
            <th>Часы</th>
            <th>Состояние</th>
            <th>Номер автомобиля</th>
            <th>Цена</th>
            <th>Цвет класса</th>
          </tr>
        </thead>
        <tbody>
          {filterDataByMonthAndYear(data, month, year).map((item) => (
            <tr key={item._id}>
              <td>{item.date}</td>
              <td>{item.brand}</td>
              <td>{item.driver}</td>
              <td>{item.customer}</td>
              <td>{item.routeNumber}</td>
              <td>{item.hours}</td>
              <td>{item.isTrue ? "Да" : "Нет"}</td>
              <td>{item.vehicleNumber}</td>
              <td>{item.price}</td>
              <td>{item.colorClass}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MonthlyData;
