import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = () => {
  const [data, setData] = useState([]);
  const [editItem, setEditItem] = useState(null); // Хранит текущее редактируемое значение
  const [form, setForm] = useState({
    date: "",
    mark: "",
    driver: "",
    customer: "",
    number: "",
    hours: "",
    boolean: "",
    license: "",
    price: "",
  });
  const [isAddMode, setIsAddMode] = useState(false); // Флаг для переключения между добавлением и редактированием

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: value,
    });
  };

  const handleAddClick = () => {
    setIsAddMode(true);
    setEditItem(null);
    setForm({
      date: "",
      mark: "",
      driver: "",
      customer: "",
      number: "",
      hours: "",
      boolean: "",
      license: "",
      price: "",
    });
  };

  const handleSaveClick = async () => {
    try {
      if (editItem) {
        // Обновление существующего элемента
        await axios.put(`http://localhost:5000/api/data/${editItem._id}`, form);
        setData(data.map((item) => (item._id === editItem._id ? form : item)));
        setEditItem(null);
      } else {
        // Добавление нового элемента
        await axios.post("http://localhost:5000/api/data", form);
        setData([...data, form]);
      }
      setIsAddMode(false);
      setForm({
        date: "",
        mark: "",
        driver: "",
        customer: "",
        number: "",
        hours: "",
        boolean: "",
        license: "",
        price: "",
      });
    } catch (error) {
      console.error("Error saving data:", error);
    }
  };

  const handleEditClick = (item) => {
    setEditItem(item);
    setIsAddMode(true);
    setForm({ ...item });
  };

  return (
    <div>
      <h1>Data Table</h1>
      <button onClick={handleAddClick}>Add Data</button>
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr
            style={{ position: "sticky", top: 0, backgroundColor: "#f4f4f4" }}
          >
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>Date</th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>Mark</th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>Driver</th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>
              Customer
            </th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>Number</th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>Hours</th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>
              Boolean
            </th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>
              License
            </th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>Price</th>
            <th style={{ border: "1px solid #ddd", padding: "8px" }}>
              Actions
            </th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                {item.date}
              </td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                {item.mark}
              </td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                {item.driver}
              </td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                {item.customer}
              </td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                {item.number}
              </td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                {item.hours}
              </td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                {item.boolean ? "TRUE" : "FALSE"}
              </td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                {item.license}
              </td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                {item.price}
              </td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                <button onClick={() => handleEditClick(item)}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {(isAddMode || editItem) && (
        <div style={{ marginTop: "20px" }}>
          <h2>{editItem ? "Edit Item" : "Add Item"}</h2>
          <form>
            <input
              name="date"
              value={form.date}
              onChange={handleInputChange}
              placeholder="Date"
            />
            <input
              name="mark"
              value={form.mark}
              onChange={handleInputChange}
              placeholder="Mark"
            />
            <input
              name="driver"
              value={form.driver}
              onChange={handleInputChange}
              placeholder="Driver"
            />
            <input
              name="customer"
              value={form.customer}
              onChange={handleInputChange}
              placeholder="Customer"
            />
            <input
              name="number"
              value={form.number}
              onChange={handleInputChange}
              placeholder="Number"
            />
            <input
              name="hours"
              value={form.hours}
              onChange={handleInputChange}
              placeholder="Hours"
            />
            <input
              name="boolean"
              value={form.boolean}
              onChange={handleInputChange}
              placeholder="Boolean"
            />
            <input
              name="license"
              value={form.license}
              onChange={handleInputChange}
              placeholder="License"
            />
            <input
              name="price"
              value={form.price}
              onChange={handleInputChange}
              placeholder="Price"
            />
            <button type="button" onClick={handleSaveClick}>
              {editItem ? "Save Changes" : "Add Data"}
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Home;
