// Home.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const formatDate = (date) => {
  const d = new Date(date);
  if (isNaN(d.getTime())) return "Invalid Date";
  return d.toLocaleDateString();
};

const Home = () => {
  const [data, setData] = useState([]);
  const [form, setForm] = useState({
    date: "",
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    status: false,
    licensePlate: "",
    price: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({
      ...form,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/data", form);
      setData([...data, response.data]); // Обновляем список данных с новым элементом
      setForm({
        date: "",
        brand: "",
        driver: "",
        customer: "",
        routeNumber: "",
        hours: "",
        status: false,
        licensePlate: "",
        price: "",
      }); // Сбрасываем форму
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  return (
    <div>
      <h1>Data Table</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="date"
          name="date"
          value={form.date}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="brand"
          value={form.brand}
          onChange={handleChange}
          placeholder="Марка"
          required
        />
        <input
          type="text"
          name="driver"
          value={form.driver}
          onChange={handleChange}
          placeholder="ВОДИТЕЛЬ"
          required
        />
        <input
          type="text"
          name="customer"
          value={form.customer}
          onChange={handleChange}
          placeholder="ЗАКАЗЧИК"
          required
        />
        <input
          type="text"
          name="routeNumber"
          value={form.routeNumber}
          onChange={handleChange}
          placeholder="№ пут"
          required
        />
        <input
          type="number"
          name="hours"
          value={form.hours}
          onChange={handleChange}
          placeholder="ЧАСЫ"
          required
        />
        <label>
          FALSE/true
          <input
            type="checkbox"
            name="status"
            checked={form.status}
            onChange={handleChange}
          />
        </label>
        <input
          type="text"
          name="licensePlate"
          value={form.licensePlate}
          onChange={handleChange}
          placeholder="Гос №"
          required
        />
        <input
          type="number"
          name="price"
          value={form.price}
          onChange={handleChange}
          placeholder="ЦЕНА"
          required
        />
        <button type="submit">Add Data</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>Дата</th>
            <th>Марка</th>
            <th>ВОДИТЕЛЬ</th>
            <th>ЗАКАЗЧИК</th>
            <th>№ пут</th>
            <th>ЧАСЫ</th>
            <th>FALSE/true</th>
            <th>Гос №</th>
            <th>ЦЕНА</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td>{formatDate(item.date)}</td>
              <td>{item.brand}</td>
              <td>{item.driver}</td>
              <td>{item.customer}</td>
              <td>{item.routeNumber}</td>
              <td>{item.hours}</td>
              <td>{item.status ? "TRUE" : "FALSE"}</td>
              <td>{item.licensePlate}</td>
              <td>{item.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
