import React, { useState, useEffect } from "react";
import axios from "axios";
import { hardcodedData } from "./hardcodedData";
import { useNavigate } from "react-router-dom";
import "./styles.css";

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [newDriver, setNewDriver] = useState("");
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split("T")[0],
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
    colorClass: "",
  });
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().split("T")[0]
  );
  const [username, setUsername] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/drivers");
        setDrivers(response.data);
      } catch (error) {
        console.error("Error fetching drivers:", error);
      }
    };

    fetchData();
    fetchDrivers();

    const fetchUsername = async () => {
      try {
        const token = localStorage.getItem("token");
        if (token) {
          const response = await axios.get("http://localhost:5000/api/user", {
            headers: { Authorization: `Bearer ${token}` },
          });
          setUsername(response.data.username || "User");
        }
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    fetchUsername();
  }, []);

  const sortedData = data.sort((a, b) => new Date(a.date) - new Date(b.date));

  const addEntriesForSelectedDate = async () => {
    try {
      const newEntries = hardcodedData.map((item) => ({
        ...item,
        date: selectedDate,
      }));

      await Promise.all(
        newEntries.map((entry) =>
          axios.post("http://localhost:5000/api/data", entry)
        )
      );

      const response = await axios.get("http://localhost:5000/api/data");
      setData(response.data);
    } catch (error) {
      console.error("Error adding entries:", error);
    }
  };

  const addNewItem = async () => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/data",
        newItem
      );
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  const addNewItemWithClass = async (className) => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", {
        ...newItem,
        colorClass: className,
      });
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data with class:", error);
    }
  };

  const handleInputChange = (e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;

    setData(
      data.map((item) =>
        item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
      )
    );

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...data.find((item) => item._id === itemId),
        [fieldName]: updatedValue,
      })
      .catch((error) => console.error("Error saving data:", error));
  };

  const handleDateChange = (e, itemId) => {
    const { value } = e.target;

    setData(
      data.map((item) =>
        item._id === itemId ? { ...item, date: value } : item
      )
    );

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...data.find((item) => item._id === itemId),
        date: value,
      })
      .catch((error) => console.error("Error saving data:", error));
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div>
      <h2>Welcome, {username}</h2>
      <button onClick={handleLogout}>Logout</button>
      <div>
        <h3>Add Entries for Selected Date</h3>
        <button onClick={addEntriesForSelectedDate}>Add Entries</button>
        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
        />
      </div>
      <div>
        <h3>Add New Item</h3>
        <button onClick={() => addNewItemWithClass("red")}>Add Red Item</button>
        <button onClick={() => addNewItemWithClass("green")}>
          Add Green Item
        </button>
        <button onClick={() => addNewItemWithClass("blue")}>
          Add Blue Item
        </button>
      </div>
      <div>
        <h3>New Item</h3>
        <input
          type="text"
          value={newItem.date}
          onChange={(e) => setNewItem({ ...newItem, date: e.target.value })}
        />
        <input
          type="text"
          value={newItem.brand}
          onChange={(e) => setNewItem({ ...newItem, brand: e.target.value })}
        />
        <input
          type="text"
          value={newItem.driver}
          onChange={(e) => setNewItem({ ...newItem, driver: e.target.value })}
        />
        <input
          type="text"
          value={newItem.customer}
          onChange={(e) => setNewItem({ ...newItem, customer: e.target.value })}
        />
        <input
          type="text"
          value={newItem.routeNumber}
          onChange={(e) =>
            setNewItem({ ...newItem, routeNumber: e.target.value })
          }
        />
        <input
          type="text"
          value={newItem.hours}
          onChange={(e) => setNewItem({ ...newItem, hours: e.target.value })}
        />
        <input
          type="checkbox"
          checked={newItem.isTrue}
          onChange={(e) => setNewItem({ ...newItem, isTrue: e.target.checked })}
        />
        <input
          type="text"
          value={newItem.vehicleNumber}
          onChange={(e) =>
            setNewItem({ ...newItem, vehicleNumber: e.target.value })
          }
        />
        <input
          type="text"
          value={newItem.price}
          onChange={(e) => setNewItem({ ...newItem, price: e.target.value })}
        />
        <input
          type="text"
          value={newItem.colorClass}
          onChange={(e) =>
            setNewItem({ ...newItem, colorClass: e.target.value })
          }
        />
        <button onClick={addNewItem}>Add New Item</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Brand</th>
            <th>Driver</th>
            <th>Customer</th>
            <th>Route Number</th>
            <th>Hours</th>
            <th>Is True</th>
            <th>Vehicle Number</th>
            <th>Price</th>
            <th>Color Class</th>
          </tr>
        </thead>
        <tbody>
          {sortedData.map((item) => (
            <tr key={item._id} className={item.colorClass}>
              <td>
                <input
                  type="date"
                  value={item.date}
                  onChange={(e) => handleDateChange(e, item._id)}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.brand}
                  onChange={(e) => handleInputChange(e, item._id, "brand")}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.driver}
                  onChange={(e) => handleInputChange(e, item._id, "driver")}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.customer}
                  onChange={(e) => handleInputChange(e, item._id, "customer")}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.routeNumber}
                  onChange={(e) =>
                    handleInputChange(e, item._id, "routeNumber")
                  }
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.hours}
                  onChange={(e) => handleInputChange(e, item._id, "hours")}
                />
              </td>
              <td>
                <input
                  type="checkbox"
                  checked={item.isTrue}
                  onChange={(e) => handleInputChange(e, item._id, "isTrue")}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.vehicleNumber}
                  onChange={(e) =>
                    handleInputChange(e, item._id, "vehicleNumber")
                  }
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.price}
                  onChange={(e) => handleInputChange(e, item._id, "price")}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.colorClass}
                  onChange={(e) => handleInputChange(e, item._id, "colorClass")}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
