import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { hardcodedData } from "./hardcodedData";
import "./styles.css";
import { format, addMonths, subMonths } from "date-fns";
import { ru } from "date-fns/locale";

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [users, setUsers] = useState([]);
  const [newDriver, setNewDriver] = useState("");
  const [selectedRows, setSelectedRows] = useState({});
  const [newItem, setNewItem] = useState({
    doneCheck: false,
    date: new Date().toISOString().split("T")[0],
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
    comment: "",
    user: "",
    colorClass: "",
  });
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().split("T")[0]
  );
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const currentDayRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/drivers");
        setDrivers(response.data);
      } catch (error) {
        console.error("Error fetching drivers:", error);
      }
    };
    const fetchUsers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/users");
        setUsers(response.data);
      } catch (error) {
        console.error("Error fetching setUsers:", error);
      }
    };
    fetchUsers();
    fetchData();
    fetchDrivers();
  }, []);

  useEffect(() => {
    if (currentDayRef.current) {
      currentDayRef.current.scrollIntoView({
        behavior: "smooth",
        block: "start", // Или "nearest"
      });
    }
  }, [data]);

  const addEntriesForSelectedDate = async () => {
    try {
      const newEntries = hardcodedData.map((item) => ({
        ...item,
        date: selectedDate,
        createdBy: "Static",
      }));

      await Promise.all(
        newEntries.map((entry) =>
          axios.post("http://localhost:5000/api/data", entry)
        )
      );

      const response = await axios.get("http://localhost:5000/api/data");
      setData(response.data);
    } catch (error) {
      console.error("Error adding entries:", error);
    }
  };

  const addNewItem = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", {
        ...newItem,
        updatedBy: localStorage.getItem("username"),
        createdBy: localStorage.getItem("username"),
      });
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  const handleInputChange = (e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;
    const username = localStorage.getItem("username");
    const itemUser = "2323";
    // Проверка для поля с часами
    if (fieldName === "hours" || fieldName === "routeNumber") {
      if (username !== itemUser) {
        alert("У вас нет прав для редактирования часов.");
        return;
      }
    }

    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
    );
    setData(updatedData);

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        [fieldName]: updatedValue,
        updatedBy: username,
      })
      .catch((error) => console.error("Error saving data:", error));
  };

  const addNewItemWithClass = async (className) => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", {
        ...newItem,
        colorClass: className,
        updatedBy: localStorage.getItem("username"),
      });
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data with class:", error);
    }
  };
  
  const handleRowSelection = async (e, itemId) => {
    const { checked } = e.target;

    // Обновляем состояние чекбоксов
    setSelectedRows((prevSelectedRows) => ({
      ...prevSelectedRows,
      [itemId]: checked,
    }));

    // Обновляем данные в состоянии
    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, doneCheck: checked } : item
    );
    setData(updatedData);

    try {
      await axios.put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        doneCheck: checked,
      });
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  const handleCheckboxChange = async (e, itemId) => {
    const { checked } = e.target;
    const username = localStorage.getItem("username");
    if (username !== "2323") {
      alert("У вас нет прав для изменения состояния чекбокса.");
      return;
    }
    if (!checked) {
      const isConfirmed = window.confirm(
        "Вы уверены, что хотите снять чекбокс?"
      );
      if (!isConfirmed) {
        return;
      }
    }

    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, isTrue: checked } : item
    );
    setData(updatedData);

    try {
      await axios.put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        isTrue: checked,
      });
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  const handleDeleteWithConfirmation = async (id) => {
    const isConfirmed = window.confirm(
      "Are you sure you want to delete this item?"
    );
    if (isConfirmed) {
      try {
        await axios.delete(`http://localhost:5000/api/data/${id}`);
        setData(data.filter((item) => item._id !== id));
      } catch (error) {
        console.error("Error deleting data:", error);
      }
    }
  };

  const handleDateChange = (e, itemId) => {
    const { value } = e.target;
    const username = localStorage.getItem("username");

    if (username !== "2323") {
      alert("У вас нет прав для изменения состояния чекбокса.");
      return;
    }
    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, date: value } : item
    );
    setData(updatedData);

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        date: value,
        updatedBy: username,
      })
      .catch((error) => console.error("Error updating date:", error));
  };

  const handleAddNewDriver = async () => {
    if (newDriver.trim() === "") return;

    try {
      const response = await axios.post("http://localhost:5000/api/drivers", {
        name: newDriver,
      });
      setDrivers([...drivers, response.data]);
      setNewDriver("");
    } catch (error) {
      console.error("Error adding driver:", error);
    }
  };

  const groupByDay = (data) => {
    return data.reduce((groups, item) => {
      const date = new Date(item.date);
      const day = date.getDate();

      if (!groups[day]) groups[day] = [];
      groups[day].push(item);
      return groups;
    }, {});
  };

  const getMonthName = (date) => {
    const monthNamesInGenitive = {
      January: "Января",
      February: "Февраля",
      March: "Марта",
      April: "Апреля",
      May: "Мая",
      June: "Июня",
      July: "Июля",
      August: "Августа",
      September: "Сентября",
      October: "Октября",
      November: "Ноября",
      December: "Декабря",
    };
    const month = format(date, "MMMM", { locale: ru });
    return monthNamesInGenitive[month] || month;
  };

  const handleMonthChange = (direction) => {
    setCurrentMonth((prevMonth) =>
      direction === "next"
        ? addMonths(prevMonth, 1)
        : subMonths(prevMonth, 1)
    );
  };

  const handleDateSelect = (date) => {
    setSelectedDate(date);
    addEntriesForSelectedDate();
  };

  const isToday = (date) => {
    const today = new Date();
    const d = new Date(date);
    return (
      today.getDate() === d.getDate() &&
      today.getMonth() === d.getMonth() &&
      today.getFullYear() === d.getFullYear()
    );
  };

  return (
    <div className="home-container">
      <h1>{getMonthName(currentMonth)}</h1>
      <button onClick={() => handleMonthChange("prev")}>Предыдущий месяц</button>
      <button onClick={() => handleMonthChange("next")}>Следующий месяц</button>

      <button onClick={() => handleAddNewDriver()}>Добавить водителя</button>
      <input
        type="text"
        value={newDriver}
        onChange={(e) => setNewDriver(e.target.value)}
        placeholder="Новый водитель"
      />

      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>Дата</th>
              <th>Бренд</th>
              <th>Водитель</th>
              <th>Клиент</th>
              <th>Номер маршрута</th>
              <th>Часы</th>
              <th>Транспортное средство</th>
              <th>Цена</th>
              <th>Комментарий</th>
              <th>Изменено</th>
              <th>Выделить</th>
              <th>Удалить</th>
            </tr>
          </thead>
          <tbody>
            {Object.keys(groupByDay(data)).map((day) =>
              groupByDay(data)[day].map((item) => (
                <tr
                  key={item._id}
                  ref={isToday(item.date) ? currentDayRef : null}
                  className={isToday(item.date) ? "highlight" : ""}
                >
                  <td>{item.date}</td>
                  <td>
                    <input
                      type="text"
                      value={item.brand}
                      onChange={(e) => handleInputChange(e, item._id, "brand")}
                    />
                  </td>
                  <td>
                    <select
                      value={item.driver}
                      onChange={(e) => handleInputChange(e, item._id, "driver")}
                    >
                      <option value="">Выберите водителя</option>
                      {drivers.map((driver) => (
                        <option key={driver._id} value={driver.name}>
                          {driver.name}
                        </option>
                      ))}
                    </select>
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.customer}
                      onChange={(e) => handleInputChange(e, item._id, "customer")}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.routeNumber}
                      onChange={(e) => handleInputChange(e, item._id, "routeNumber")}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.hours}
                      onChange={(e) => handleInputChange(e, item._id, "hours")}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.vehicleNumber}
                      onChange={(e) => handleInputChange(e, item._id, "vehicleNumber")}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.price}
                      onChange={(e) => handleInputChange(e, item._id, "price")}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.comment}
                      onChange={(e) => handleInputChange(e, item._id, "comment")}
                    />
                  </td>
                  <td>{item.updatedBy}</td>
                  <td>
                    <input
                      type="checkbox"
                      checked={item.doneCheck}
                      onChange={(e) => handleCheckboxChange(e, item._id)}
                    />
                  </td>
                  <td>
                    <button onClick={() => handleDeleteWithConfirmation(item._id)}>Удалить</button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Home;
