import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { format } from "date-fns";
import { ru } from "date-fns/locale";
import { useNavigate } from "react-router-dom";
import "./styles.css";

const Home = () => {
  const [data, setData] = useState([]);
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const navigate = useNavigate();
  const currentDayRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (currentDayRef.current) {
      currentDayRef.current.scrollIntoView({
        behavior: "smooth",
        block: "start",
      });
    }
  }, [data]);

  const groupByMonth = (data) => {
    return data.reduce((groups, item) => {
      const date = new Date(item.date);
      const monthYear = format(date, "MMMM yyyy", { locale: ru });

      if (!groups[monthYear]) groups[monthYear] = [];
      groups[monthYear].push(item);

      return groups;
    }, {});
  };

  const getCurrentMonthName = () => {
    return format(currentMonth, "MMMM yyyy", { locale: ru });
  };

  const currentMonthData = groupByMonth(data)[getCurrentMonthName()] || [];

  return (
    <div>
      <div className="header">
        <h1>Data Table for {getCurrentMonthName()}</h1>
        <button
          onClick={() => navigate(`/month/${format(currentMonth, "yyyy-MM")}`)}
        >
          View Previous Months
        </button>
      </div>
      <div className="data">
        <div ref={currentDayRef}>
          <table>
            <thead>
              <tr>
                <th>Date</th>
                <th>Brand</th>
                <th>Driver</th>
                <th>Customer</th>
                <th>Route Number</th>
                <th>Hours</th>
                <th>True/False</th>
                <th>Vehicle Number</th>
                <th>Price</th>
                <th>Last Edited By</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {currentMonthData.map((item) => (
                <tr key={item._id}>
                  <td>{item.date}</td>
                  <td>{item.brand}</td>
                  <td>{item.driver}</td>
                  <td>{item.customer}</td>
                  <td>{item.routeNumber}</td>
                  <td>{item.hours}</td>
                  <td>{item.isTrue ? "True" : "False"}</td>
                  <td>{item.vehicleNumber}</td>
                  <td>{item.price}</td>
                  <td>{item.updatedBy}</td>
                  <td>
                    <button
                      onClick={() =>
                        // Handle delete or other actions
                        console.log("Delete item", item._id)
                      }
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Home;
