import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { hardcodedData } from "./hardcodedData"; // Убедитесь, что путь правильный
import "./styles.css"; // Импорт стилей для кнопок
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [newDriver, setNewDriver] = useState("");
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split("T")[0],
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
    colorClass: "", // Добавлен colorClass
  });
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().split("T")[0]
  );

  const navigate = useNavigate();
  const currentDayRef = useRef(null);

  // Получение данных с сервера
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/drivers");
        setDrivers(response.data);
      } catch (error) {
        console.error("Error fetching drivers:", error);
      }
    };

    fetchData();
    fetchDrivers();
  }, []);

  // Прокрутка к текущему дню после загрузки данных
  useEffect(() => {
    if (currentDayRef.current) {
      currentDayRef.current.scrollIntoView({ behavior: "smooth", block: "start" });
    }
  }, [data]);

  // Функция выхода из системы
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  // Сортировка данных по дате и затем по водителям
  const sortedData = data.sort((a, b) => {
    const dateComparison = new Date(a.date) - new Date(b.date);
    if (dateComparison !== 0) return dateComparison;
    return a.driver.localeCompare(b.driver);
  });

  // Функция для добавления строк на каждый новый день
  const addEntriesForSelectedDate = async () => {
    try {
      const newEntries = hardcodedData.map((item) => ({
        ...item,
        date: selectedDate,
      }));

      await Promise.all(
        newEntries.map((entry) =>
          axios.post("http://localhost:5000/api/data", entry)
        )
      );

      const response = await axios.get("http://localhost:5000/api/data");
      setData(response.data);
    } catch (error) {
      console.error("Error adding entries:", error);
    }
  };

  // Добавление одной строки данных
  const handleDateChange = (e, itemId) => {
    handleChangeConfirmation(() => {
      const { value } = e.target;
      setData(
        data.map((item) =>
          item._id === itemId ? { ...item, date: value, hasChanged: true } : item
        )
      );
      axios
        .put(`http://localhost:5000/api/data/${itemId}`, {
          ...data.find((item) => item._id === itemId),
          date: value,
        })
        .catch((error) => console.error("Error updating date:", error));
    });
  };
  
  const handleDelete = async (itemId) => {
    if (window.confirm("Are you sure you want to delete this item?")) {
      try {
        await axios.delete(`http://localhost:5000/api/data/${itemId}`);
        setData(data.filter(item => item._id !== itemId));
      } catch (error) {
        console.error("Error deleting data:", error);
      }
    }
  };
  
  const addNewItem = async () => {
    handleChangeConfirmation(async () => {
      try {
        const response = await axios.post("http://localhost:5000/api/data", newItem);
        setData([...data, response.data]);
        setNewItem({
          date: new Date().toISOString().split("T")[0],
          brand: "",
          driver: "",
          customer: "",
          routeNumber: "",
          hours: "",
          isTrue: false,
          vehicleNumber: "",
          price: "",
          colorClass: "",
        });
      } catch (error) {
        console.error("Error adding data:", error);
      }
    });
  };
  

  // Добавление строки с определенным классом
  const addNewItemWithClass = async (className) => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", {
        ...newItem,
        colorClass: className, // Добавляем класс
      });
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data with class:", error);
    }
  };


  const handleInputChange = (e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;

    setData(
      data.map((item) =>
        item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
      )
    );

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...data.find((item) => item._id === itemId),
        [fieldName]: updatedValue,
      })
      .catch((error) => console.error("Error saving data:", error));
  };


  const handleAddNewDriver = async () => {
    if (newDriver.trim() === "") return;

    try {
      const response = await axios.post("http://localhost:5000/api/drivers", {
        name: newDriver,
      });
      setDrivers([...drivers, response.data]);
      setNewDriver("");
    } catch (error) {
      console.error("Error adding driver:", error);
    }
  };

  // Функция для группировки данных по месяцам и дням
  const groupByMonthAndDay = (data) => {
    return data.reduce((groups, item) => {
      const date = new Date(item.date);
      const monthYear = date.toLocaleString("default", { month: "long" });
      const day = date.getDate();

      if (!groups[monthYear]) {
        groups[monthYear] = {};
      }

      if (!groups[monthYear][day]) {
        groups[monthYear][day] = [];
      }

      groups[monthYear][day].push(item);
      return groups;
    }, {});
  };

  // Определяем текущий день для прокрутки
  const isToday = (itemDate) => {
    const today = new Date();
    const itemDay = new Date(itemDate);
    return (
      today.getDate() === itemDay.getDate() &&
      today.getMonth() === itemDay.getMonth() &&
      today.getFullYear() === itemDay.getFullYear()
    );
  };

  return (
    <div>
      <div className="header">
        <h1>Data Table</h1>
        <div className="buttons">
          <button onClick={addEntriesForSelectedDate}>
            Add 37 Entries for Selected Date
          </button>
          <button onClick={addNewItem}>Add New Item</button>
          <button onClick={() => addNewItemWithClass("highlight")}>
            Add New Item with Highlight
          </button>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </div>
      <div className="controls">
        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
        />
        <input
          type="text"
          value={newDriver}
          onChange={(e) => setNewDriver(e.target.value)}
          placeholder="Enter new driver name"
        />
        <button onClick={handleAddNewDriver}>Add New Driver</button>
      </div>
      {Object.entries(groupByMonthAndDay(sortedData)).map(([month, days]) => (
        <div key={month}>
          <h2>{month}</h2>
          {Object.entries(days).map(([day, items]) => (
            <div
              key={day}
              ref={items.some((item) => isToday(item.date)) ? currentDayRef : null}
            >
              <h3>Day {day}</h3>
              <table>
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Brand</th>
                    <th>Driver</th>
                    <th>Customer</th>
                    <th>Route Number</th>
                    <th>Hours</th>
                    <th>True/False</th>
                    <th>Vehicle Number</th>
                    <th>Price</th>
                    <th>Actions</th> {/* Добавляем колонку для действий */}
                  </tr>
                </thead>
                <tbody>
                  {items
                    .sort((a, b) => {
                      if (a.colorClass === "highlight" && b.colorClass !== "highlight") return 1;
                      if (a.colorClass !== "highlight" && b.colorClass === "highlight") return -1;
                      return a.driver.localeCompare(b.driver);
                    })
                    .map((item) => (
                      <tr key={item._id} className={item.colorClass || ""}>
                        <td>
                          <input
                            type="date"
                            value={item.date || ""}
                            onChange={(e) => handleDateChange(e, item._id)}
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.brand || ""}
                            onChange={(e) => handleInputChange(e, item._id, "brand")}
                          />
                        </td>
                        <td>
                          <select
                            value={item.driver || ""}
                            onChange={(e) => handleInputChange(e, item._id, "driver")}
                          >
                            <option value="">Select Driver</option>
                            {drivers.map((driver) => (
                              <option key={driver._id} value={driver._id}>
                                {driver.name}
                              </option>
                            ))}
                          </select>
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.customer || ""}
                            onChange={(e) => handleInputChange(e, item._id, "customer")}
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.routeNumber || ""}
                            onChange={(e) => handleInputChange(e, item._id, "routeNumber")}
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.hours || ""}
                            onChange={(e) => handleInputChange(e, item._id, "hours")}
                          />
                        </td>
                        <td>
                          <input
                            type="checkbox"
                            checked={item.isTrue || false}
                            onChange={(e) => handleInputChange(e, item._id, "isTrue")}
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.vehicleNumber || ""}
                            onChange={(e) => handleInputChange(e, item._id, "vehicleNumber")}
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.price || ""}
                            onChange={(e) => handleInputChange(e, item._id, "price")}
                          />
                        </td>
                        <td>
                          <button onClick={() => handleDelete(item._id)}>Delete</button> {/* Кнопка удаления */}
                        </td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Home;
