import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import Login from "./components/Login";
import Register from "./components/Register";
import Home from "./components/Home";
import MonthPage from "./components/MonthPage"; // Импортируем новый компонент
const userName = localStorage.getItem("username");

function App() {
  const [auth, setAuth] = useState({
    isAuthenticated: false,
    user: null,
  });

  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem("token");
      const userName = localStorage.getItem("username");
      if (token) {
        try {
          setAuth({
            isAuthenticated: true,
            user: userName,
          });
        } catch (error) {
          console.error("Error fetching user data:", error);
          localStorage.removeItem("token");
          localStorage.removeItem("username");
          setAuth({
            isAuthenticated: false,
            user: null,
          });
        }
      }
    };

    checkAuth();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    setAuth({ isAuthenticated: false, user: null });
  };

  return (
    <Router>
      <div>
        {auth.isAuthenticated && (
          <nav>
            <span>Welcome, {userName}</span>
            <button onClick={handleLogout}>Logout</button>
          </nav>
        )}
        <Routes>
          <Route
            path="/login"
            element={
              auth.isAuthenticated ? (
                <Navigate to="/" />
              ) : (
                <Login setAuth={setAuth} />
              )
            }
          />
          <Route
            path="/register"
            element={auth.isAuthenticated ? <Navigate to="/" /> : <Register />}
          />
          <Route
            path="/"
            element={auth.isAuthenticated ? <Home /> : <Navigate to="/login" />}
          />
          <Route
            path="/month/:monthName"
            element={
              auth.isAuthenticated ? <MonthPage /> : <Navigate to="/login" />
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
