import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = () => {
  // Состояние для данных
  const [data, setData] = useState([]);
  // Состояние для формы
  const [form, setForm] = useState({
    date: "",
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    status: false,
    licensePlate: "",
    price: "",
  });

  // Загрузка данных при монтировании компонента
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  // Обработчик отправки формы
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/data", form);
      setData([...data, response.data]); // Обновляем состояние данных
      setForm({
        date: "",
        brand: "",
        driver: "",
        customer: "",
        routeNumber: "",
        hours: "",
        status: false,
        licensePlate: "",
        price: "",
      }); // Сбрасываем форму
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  return (
    <div>
      <h1>Data Table</h1>
      {/* Форма для добавления новых данных */}
      <form onSubmit={handleSubmit}>
        <input
          type="date"
          value={form.date}
          onChange={(e) => setForm({ ...form, date: e.target.value })}
          placeholder="Date"
          required
        />
        <input
          type="text"
          value={form.brand}
          onChange={(e) => setForm({ ...form, brand: e.target.value })}
          placeholder="Brand"
          required
        />
        <input
          type="text"
          value={form.driver}
          onChange={(e) => setForm({ ...form, driver: e.target.value })}
          placeholder="Driver"
          required
        />
        <input
          type="text"
          value={form.customer}
          onChange={(e) => setForm({ ...form, customer: e.target.value })}
          placeholder="Customer"
          required
        />
        <input
          type="text"
          value={form.routeNumber}
          onChange={(e) => setForm({ ...form, routeNumber: e.target.value })}
          placeholder="Route Number"
          required
        />
        <input
          type="number"
          value={form.hours}
          onChange={(e) => setForm({ ...form, hours: e.target.value })}
          placeholder="Hours"
          required
        />
        <label>
          Status:
          <input
            type="checkbox"
            checked={form.status}
            onChange={(e) => setForm({ ...form, status: e.target.checked })}
          />
        </label>
        <input
          type="text"
          value={form.licensePlate}
          onChange={(e) => setForm({ ...form, licensePlate: e.target.value })}
          placeholder="License Plate"
          required
        />
        <input
          type="number"
          value={form.price}
          onChange={(e) => setForm({ ...form, price: e.target.value })}
          placeholder="Price"
          required
        />
        <button type="submit">Add Data</button>
      </form>

      {/* Таблица для отображения данных */}
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Brand</th>
            <th>Driver</th>
            <th>Customer</th>
            <th>Route Number</th>
            <th>Hours</th>
            <th>Status</th>
            <th>License Plate</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td>{new Date(item.date).toLocaleDateString()}</td>
              <td>{item.brand}</td>
              <td>{item.driver}</td>
              <td>{item.customer}</td>
              <td>{item.routeNumber}</td>
              <td>{item.hours}</td>
              <td>{item.status ? "TRUE" : "FALSE"}</td>
              <td>{item.licensePlate}</td>
              <td>{item.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
