import React, { useState, useEffect } from "react";
import axios from "axios";
import { format } from "date-fns";
import { ru } from "date-fns/locale";

const MonthlyData = () => {
  const [data, setData] = useState([]);
  const [selectedMonth, setSelectedMonth] = useState(new Date());
  const [months, setMonths] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    const monthsList = [];
    for (let i = -12; i <= 0; i++) {
      const month = new Date();
      month.setMonth(month.getMonth() + i);
      monthsList.push({
        label: format(month, "MMMM yyyy", { locale: ru }),
        value: month,
      });
    }
    setMonths(monthsList);
  }, []);

  const handleMonthChange = (event) => {
    setSelectedMonth(new Date(event.target.value));
  };

  const filteredData = data.filter((item) => {
    const itemDate = new Date(item.date);
    return (
      itemDate.getFullYear() === selectedMonth.getFullYear() &&
      itemDate.getMonth() === selectedMonth.getMonth()
    );
  });

  const getMonthName = (date) => {
    const monthNamesInGenitive = {
      январь: "января",
      февраль: "февраля",
      март: "марта",
      апрель: "апреля",
      май: "мая",
      июнь: "июня",
      июль: "июля",
      август: "августа",
      сентябрь: "сентября",
      октябрь: "октября",
      ноябрь: "ноября",
      декабрь: "декабря",
    };
    const monthName = format(date, "MMMM", { locale: ru });
    return monthNamesInGenitive[monthName] || monthName;
  };

  return (
    <div>
      <div className="header">
        <h1>Monthly Data</h1>
        <select
          onChange={handleMonthChange}
          value={format(selectedMonth, "yyyy-MM")}
        >
          {months.map((month, index) => (
            <option key={index} value={format(month.value, "yyyy-MM")}>
              {month.label}
            </option>
          ))}
        </select>
      </div>
      <div className="data">
        <h2>
          {getMonthName(selectedMonth)} {selectedMonth.getFullYear()}
        </h2>
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Brand</th>
              <th>Driver</th>
              <th>Customer</th>
              <th>Route Number</th>
              <th>Hours</th>
              <th>True/False</th>
              <th>Vehicle Number</th>
              <th>Price</th>
              <th>Last Edited By</th>
            </tr>
          </thead>
          <tbody>
            {filteredData.map((item) => (
              <tr key={item._id}>
                <td>{format(new Date(item.date), "yyyy-MM-dd")}</td>
                <td>{item.brand}</td>
                <td>{item.driver}</td>
                <td>{item.customer}</td>
                <td>{item.routeNumber}</td>
                <td>{item.hours}</td>
                <td>{item.isTrue ? "True" : "False"}</td>
                <td>{item.vehicleNumber}</td>
                <td>{item.price}</td>
                <td>{item.updatedBy || "N/A"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default MonthlyData;
