:[/*#__PURE__*/_jsxs("div",{className:"green",children:[/*#__PURE__*/_jsx("p",{children:"\u041E\u043F\u043B\u0430\u0447\u0435\u043D"}),/*#__PURE__*/_jsx("span",{className:"green-span"})]}),/*#__PURE__*/_jsxs("div",{className:"gold",children:[/*#__PURE__*/_jsx("p",{children:"\u0427\u0430\u0441\u0442\u0438\u0447\u043D\u043E"}),/*#__PURE__*/_jsx("span",{className:"gold-span"})]}),/*#__PURE__*/_jsxs("div",{className:"red",children:[/*#__PURE__*/_jsx("p",{children:"\u041D\u0435\u0442 \u043E\u043F\u043B\u0430\u0442\u044B"}),/*#__PURE__*/_jsx("span",{className:"red-span"})]}),/*#__PURE__*/_jsxs("div",{className:"yellow",children:[/*#__PURE__*/_jsx("p",{children:"\u041E\u0442\u0441\u0440\u043E\u0447\u043A\u0430"}),/*#__PURE__*/_jsx("span",{className:"yellow-span"})]})]})]})]}),/*#__PURE__*/_jsxs("h1",{children:[getMonthName(currentMonth)," ",currentMonth.getFullYear()]}),/*#__PURE__*/_jsx("div",{className:"table",children:Object.keys(groupedByDayData).map(day=>/*#__PURE__*/_jsxs("div",{className:"table-flex",children:[/*#__PURE__*/_jsxs("h2",{className:`${formattedToday===day?"is-today":""} sticky-h1`,children:[day," ",getMonthName(currentMonth)]}),/*#__PURE__*/_jsxs("table",{children:[/*#__PURE__*/_jsx("thead",{className:"sticky",children:/*#__PURE__*/_jsxs("tr",{children:[/*#__PURE__*/_jsx("th",{children:"\u0421\u0442\u0430\u0442\u0443\u0441"}),/*#__PURE__*/_jsx("th",{children:"\u041C\u0430\u0440\u043A\u0430"}),/*#__PURE__*/_jsx("th",{children:"\u0413\u043E\u0441 \u2116"}),/*#__PURE__*/_jsx("th",{children:"\u0412\u043E\u0434\u0438\u0442\u0435\u043B\u044C"}),/*#__PURE__*/_jsx("th",{children:"\u0417\u0430\u043A\u0430\u0437\u0447\u0438\u043A"}),userNameRoot==="Tanya"&&/*#__PURE__*/_jsx("th",{children:"\u2116 \u041F\u0443\u0442\u0435\u0432\u043E\u0433\u043E"}),userNameRoot==="Tanya"&&/*#__PURE__*/_jsx("th",{children:"\u0427\u0430\u0441\u044B"}),userNameRoot==="Tanya"&&/*#__PURE__*/_jsx("th",{children:"\u0421\u0442\u0430\u0442\u0443\u0441"}),/*#__PURE__*/_jsx("th",{children:"\