import React, { useState, useEffect } from "react";
import axios from "axios";
import dayjs from "dayjs"; // библиотека для работы с датами

const Home = () => {
  const [data, setData] = useState([]);
  const [newDriver, setNewDriver] = useState("");
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split("T")[0],
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  // Генерация массива данных на 37 строк
  const generateEmptyRows = (date) => {
    const rows = [];
    for (let i = 1; i <= 37; i++) {
      rows.push({
        _id: `${date}-row-${i}`,
        date,
        brand: "",
        driver: "",
        customer: "",
        routeNumber: "",
        hours: "",
        isTrue: false,
        vehicleNumber: "",
        price: "",
      });
    }
    return rows;
  };

  // Получить уникальные месяцы и дни для разделения
  const getUniqueMonths = () => {
    const months = data.map((item) => dayjs(item.date).format("MMMM YYYY"));
    return [...new Set(months)];
  };

  const getDaysInMonth = (month) => {
    return data
      .filter((item) => dayjs(item.date).format("MMMM YYYY") === month)
      .map((item) => dayjs(item.date).format("DD MMMM"));
  };

  const handleInputChange = (e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;

    setData(
      data.map((item) =>
        item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
      )
    );

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...data.find((item) => item._id === itemId),
        [fieldName]: updatedValue,
      })
      .catch((error) => console.error("Error saving data:", error));
  };

  const addNewItem = async () => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/data",
        newItem
      );
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  return (
    <div>
      <h1>Data Table</h1>
      <button onClick={addNewItem}>Add New Item</button>

      {getUniqueMonths().map((month) => (
        <div key={month}>
          <h2>{month}</h2>

          {getDaysInMonth(month).map((day) => (
            <div key={day}>
              <h3>{day}</h3>
              <table>
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Brand</th>
                    <th>Driver</th>
                    <th>Customer</th>
                    <th>Route Number</th>
                    <th>Hours</th>
                    <th>True/False</th>
                    <th>Vehicle Number</th>
                    <th>Price</th>
                  </tr>
                </thead>
                <tbody>
                  {generateEmptyRows(day).map((item, index) => (
                    <tr key={item._id}>
                      <td>{item.date}</td>
                      <td>
                        <input
                          type="text"
                          value={item.brand || ""}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "brand")
                          }
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.driver || ""}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "driver")
                          }
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.customer || ""}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "customer")
                          }
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.routeNumber || ""}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "routeNumber")
                          }
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.hours || ""}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "hours")
                          }
                        />
                      </td>
                      <td>
                        <input
                          type="checkbox"
                          checked={item.isTrue || false}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "isTrue")
                          }
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.vehicleNumber || ""}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "vehicleNumber")
                          }
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.price || ""}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "price")
                          }
                        />
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Home;
