import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import dayjs from "dayjs";

const Profile = () => {
  const [items, setItems] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [selectedDriver, setSelectedDriver] = useState("");
  const [filteredOrders, setFilteredOrders] = useState([]);
  const apiUrl = "https://bam-app-489c6c1370a9.herokuapp.com";

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`${apiUrl}/api/data`);
        setItems(response.data);

        // Получаем уникальные значения водителей
        const driverList = [
          ...new Set(response.data.map((item) => item.driver)),
        ];
        setDrivers(driverList);
      } catch (error) {
        console.error("Ошибка при загрузке данных:", error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (selectedDriver) {
      const ordersForSelectedDriver = items.filter(
        (item) => item.driver === selectedDriver && item.colorClass
      );
      setFilteredOrders(ordersForSelectedDriver);
    } else {
      setFilteredOrders([]);
    }
  }, [selectedDriver, items]);

  return (
    <div>
      <Link to="/">
        <button>Go to Home</button>
      </Link>
      <h1>Выберите водителя:</h1>
      <select onChange={(e) => setSelectedDriver(e.target.value)}>
        <option value="">Выберите водителя</option>
        {drivers.map((driver, index) => (
          <option key={index} value={driver}>
            {driver}
          </option>
        ))}
      </select>

      <h2>Заказы выбранного водителя:</h2>
      {filteredOrders.length > 0 ? (
        <ul>
          {filteredOrders.map((order) => (
            <li key={order._id}>
              <p>Дата: {dayjs(order.date).format("DD.MM.YYYY")}</p>
              <p>Бренд: {order.brand}</p>
              <p>Создано: {order.createdBy}</p>
              <p>Цвет класс: {order.colorClass}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>Нет заказов для выбранного водителя.</p>
      )}
    </div>
  );
};

export default Profile;
