import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = () => {
  const [data, setData] = useState([]);
  const [form, setForm] = useState({
    date: "",
    mark: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    carNumber: "",
    price: "",
  });
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({
      ...form,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/data", form);
      setData([...data, response.data]);
      setForm({
        date: "",
        mark: "",
        driver: "",
        customer: "",
        routeNumber: "",
        hours: "",
        isTrue: false,
        carNumber: "",
        price: "",
      });
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  const handleEdit = async (id, updatedFields) => {
    try {
      await axios.put(`http://localhost:5000/api/data/${id}`, updatedFields);
      setData(
        data.map((item) =>
          item._id === id ? { ...item, ...updatedFields } : item
        )
      );
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/data/${id}`);
      setData(data.filter((item) => item._id !== id));
    } catch (error) {
      console.error("Error deleting data:", error);
    }
  };

  return (
    <div>
      <h1>Data Table</h1>
      <button onClick={() => setEditingId("new")}>Add New Item</button>
      {editingId === "new" && (
        <form onSubmit={handleSubmit}>
          <label>
            Date:{" "}
            <input
              type="text"
              name="date"
              value={new Date().toLocaleDateString()}
              readOnly
            />
          </label>
          <label>
            Mark:{" "}
            <input
              type="text"
              name="mark"
              value={form.mark}
              onChange={handleChange}
            />
          </label>
          <label>
            Driver:{" "}
            <input
              type="text"
              name="driver"
              value={form.driver}
              onChange={handleChange}
            />
          </label>
          <label>
            Customer:{" "}
            <input
              type="text"
              name="customer"
              value={form.customer}
              onChange={handleChange}
            />
          </label>
          <label>
            Route Number:{" "}
            <input
              type="text"
              name="routeNumber"
              value={form.routeNumber}
              onChange={handleChange}
            />
          </label>
          <label>
            Hours:{" "}
            <input
              type="number"
              name="hours"
              value={form.hours}
              onChange={handleChange}
            />
          </label>
          <label>
            TRUE/FALSE:{" "}
            <input
              type="checkbox"
              name="isTrue"
              checked={form.isTrue}
              onChange={handleChange}
            />
          </label>
          <label>
            Car Number:{" "}
            <input
              type="text"
              name="carNumber"
              value={form.carNumber}
              onChange={handleChange}
            />
          </label>
          <label>
            Price:{" "}
            <input
              type="number"
              name="price"
              value={form.price}
              onChange={handleChange}
            />
          </label>
          <button type="submit">Save</button>
          <button type="button" onClick={() => setEditingId(null)}>
            Cancel
          </button>
        </form>
      )}
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Mark</th>
            <th>Driver</th>
            <th>Customer</th>
            <th>Route Number</th>
            <th>Hours</th>
            <th>TRUE/FALSE</th>
            <th>Car Number</th>
            <th>Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td>{new Date(item.date).toLocaleDateString()}</td>
              <td>{item.mark}</td>
              <td>{item.driver}</td>
              <td>{item.customer}</td>
              <td>{item.routeNumber}</td>
              <td>{item.hours}</td>
              <td>{item.isTrue ? "TRUE" : "FALSE"}</td>
              <td>{item.carNumber}</td>
              <td>{item.price}</td>
              <td>
                <button onClick={() => setEditingId(item._id)}>Edit</button>
                <button onClick={() => handleDelete(item._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
