import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { hardcodedData } from "./hardcodedData";
import "./styles.css";
import { format, addMonths, subMonths, parseISO } from "date-fns";
import { ru } from "date-fns/locale";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [users, setUsers] = useState([]);
  const [newDriver, setNewDriver] = useState("");
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split("T")[0],
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
    comment: "",
    user: "",
    colorClass: "",
  });
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().split("T")[0]
  );
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const currentDayRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/drivers");
        setDrivers(response.data);
      } catch (error) {
        console.error("Error fetching drivers:", error);
      }
    };
    const fetchUsers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/users");
        setUsers(response.data);
      } catch (error) {
        console.error("Error fetching setUsers:", error);
      }
    };
    fetchUsers();
    fetchData();
    fetchDrivers();
  }, []);
  useEffect(() => {
    const timer = setTimeout(() => {
      if (currentDayRef.current) {
        console.log("Элемент для прокрутки найден:", currentDayRef.current);
        console.log(
          "Позиция элемента:",
          currentDayRef.current.getBoundingClientRect()
        );
        currentDayRef.current.scrollIntoView({
          behavior: "smooth",
          block: "center",
          inline: "nearest",
        });
        console.log("Прокрутка выполнена");
      } else {
        console.log("Элемент для прокрутки не найден");
      }
    }, 100); // Небольшая задержка для завершения рендеринга

    return () => clearTimeout(timer);
  }, [data]); // Убедитесь, что `items` обновлен

  const addEntriesForSelectedDate = async () => {
    try {
      const newEntries = hardcodedData.map((item) => ({
        ...item,
        date: selectedDate,
        createdBy: "Static",
      }));

      await Promise.all(
        newEntries.map((entry) =>
          axios.post("http://localhost:5000/api/data", entry)
        )
      );

      const response = await axios.get("http://localhost:5000/api/data");
      setData(response.data);
    } catch (error) {
      console.error("Error adding entries:", error);
    }
  };

  const addNewItem = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", {
        ...newItem,
        updatedBy: localStorage.getItem("username"),
        createdBy: localStorage.getItem("username"),
      });
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  const handleInputChange = (e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;
    const username = localStorage.getItem("username");
    const itemUser = "2323";
    // Проверка для поля с часами
    if (fieldName === "hours" || fieldName === "routeNumber") {
      if (username !== itemUser) {
        alert("У вас нет прав для редактирования часов.");
        return;
      }
    }

    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
    );
    setData(updatedData);

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        [fieldName]: updatedValue,
        updatedBy: username,
      })
      .catch((error) => console.error("Error saving data:", error));
  };

  const addNewItemWithClass = async (className) => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", {
        ...newItem,
        colorClass: className,
        updatedBy: localStorage.getItem("username"),
      });
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data with class:", error);
    }
  };

  const handleCheckboxChange = async (e, itemId) => {
    const { checked } = e.target;
    const username = localStorage.getItem("username");
    if (username !== "2323") {
      alert("У вас нет прав для изменения состояния чекбокса.");
      return;
    }
    if (!checked) {
      const isConfirmed = window.confirm(
        "Вы уверены, что хотите снять чекбокс?"
      );
      if (!isConfirmed) {
        return;
      }
    }

    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, isTrue: checked } : item
    );
    setData(updatedData);

    try {
      await axios.put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        isTrue: checked,
      });
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  const handleDeleteWithConfirmation = async (id) => {
    const isConfirmed = window.confirm(
      "Are you sure you want to delete this item?"
    );
    if (isConfirmed) {
      try {
        await axios.delete(`http://localhost:5000/api/data/${id}`);
        setData(data.filter((item) => item._id !== id));
      } catch (error) {
        console.error("Error deleting data:", error);
      }
    }
  };

  const handleDateChange = (e, itemId) => {
    const { value } = e.target;
    const username = localStorage.getItem("username");

    if (username !== "2323") {
      alert("У вас нет прав для изменения состояния чекбокса.");
      return;
    }
    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, date: value } : item
    );
    setData(updatedData);

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        date: value,
        updatedBy: username,
      })
      .catch((error) => console.error("Error updating date:", error));
  };

  const handleAddNewDriver = async () => {
    if (newDriver.trim() === "") return;

    try {
      const response = await axios.post("http://localhost:5000/api/drivers", {
        name: newDriver,
      });
      setDrivers([...drivers, response.data]);
      setNewDriver("");
    } catch (error) {
      console.error("Error adding driver:", error);
    }
  };

  const groupByDay = (data) => {
    return data.reduce((groups, item) => {
      const date = new Date(item.date);
      const day = date.getDate();

      if (!groups[day]) groups[day] = [];
      groups[day].push(item);
      return groups;
    }, {});
  };

  const getMonthName = (date) => {
    const monthNamesInGenitive = {
      январь: "января",
      февраль: "февраля",
      март: "марта",
      апрель: "апреля",
      май: "мая",
      июнь: "июня",
      июль: "июля",
      август: "августа",
      сентябрь: "сентября",
      октябрь: "октября",
      ноябрь: "ноября",
      декабрь: "декабря",
    };
    const monthName = format(date, "MMMM", { locale: ru });
    return monthNamesInGenitive[monthName] || monthName;
  };

  const formatDate = (date) => {
    const d = new Date(date);
    const year = d.getFullYear();
    const month = String(d.getMonth() + 1).padStart(2, "0");
    const day = String(d.getDate()).padStart(2, "0");
    return `${year}-${month}-${day}`;
  };

  const handleMonthChange = (direction) => {
    const newMonth =
      direction === "prev"
        ? subMonths(currentMonth, 1)
        : addMonths(currentMonth, 1);
    setCurrentMonth(newMonth);
  };

  const sortedData = data.sort((a, b) => {
    // Если одна из строк выделена, она должна быть внизу
    if (a.colorClass === "highlight" && b.colorClass !== "highlight") return 1;
    if (a.colorClass !== "highlight" && b.colorClass === "highlight") return -1;

    // Сортировка по дате
    const dateComparison = new Date(a.date) - new Date(b.date);
    if (dateComparison !== 0) return dateComparison;

    // Сортировка по водителю
    return a.driver.localeCompare(b.driver);
  });

  // Фильтрация данных по текущему месяцу и сортировка по дням внутри месяца
  const filteredData = sortedData.filter((item) => {
    const itemDate = new Date(item.date);
    return (
      itemDate.getMonth() === currentMonth.getMonth() &&
      itemDate.getFullYear() === currentMonth.getFullYear()
    );
  });

  const groupedData = groupByDay(filteredData);

  //prev
  const firstDayRef = useRef(null);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (firstDayRef.current) {
        console.log("Элемент для прокрутки найден:", firstDayRef.current);
        console.log(
          "Позиция элемента:",
          firstDayRef.current.getBoundingClientRect()
        );

        firstDayRef.current.scrollIntoView({
          behavior: "smooth",
          block: "center",
          inline: "nearest",
        });

        console.log("Прокрутка выполнена");
      }
    }, 100); // Небольшая задержка для завершения рендеринга

    return () => clearTimeout(timer);
  }, [data]);
  const isToday = (itemDate) => {
    const today = new Date();
    const itemDay = new Date(itemDate);

    return (
      today.getDate() === itemDay.getDate() &&
      today.getMonth() === itemDay.getMonth() &&
      today.getFullYear() === itemDay.getFullYear()
    );
  };
  const getColor = (value) => {
    switch (value) {
      case "Оплачен":
        return "green";
      case "Частично Оплачен":
        return "gold";
      case "Нет Оплаты":
        return "red";
      case "Отсрочка":
        return "yellow";
      default:
        return "transporent"; // цвет по умолчанию, если ничего не выбрано
    }
  };
  const getBackColor = (value) => {
    switch (value) {
      case "Оплачен":
        return "black";
      case "Частично Оплачен":
        return "black";
      case "Нет Оплаты":
        return "black";
      case "Отсрочка":
        return "black";
      default:
        return "transporent"; // цвет по умолчанию, если ничего не выбрано
    }
  };
  const isPreviousDay = (date) => {
    const today = new Date();
    const previousDay = new Date(today);
    previousDay.setDate(previousDay.getDate() - 1);
    const itemDate = new Date(date);

    return (
      previousDay.getDate() === itemDate.getDate() &&
      previousDay.getMonth() === itemDate.getMonth() &&
      previousDay.getFullYear() === itemDate.getFullYear()
    );
  };
  return (
    <div>
      <h1>
        {getMonthName(currentMonth)} {currentMonth.getFullYear()}
      </h1>
      <button onClick={() => handleMonthChange("prev")}>Пред. Месяц</button>
      <button onClick={() => handleMonthChange("next")}>След. Месяц</button>
      <button onClick={() => setCurrentMonth(new Date())}>Текущий Месяц</button>
      <button onClick={() => addNewItemWithClass("highlight")}>
        Добавить субаренду на текущий день
      </button>
      <button onClick={addEntriesForSelectedDate} className="add-entries">
        Добавить строки на выбронный день
      </button>

      <input
        type="date"
        value={selectedDate}
        onChange={(e) => setSelectedDate(e.target.value)}
      />
      <button onClick={addNewItem}>Добавить Строку</button>

      <div className="asd">
        <input
          type="text"
          value={newDriver}
          onChange={(e) => setNewDriver(e.target.value)}
        />
        <button onClick={handleAddNewDriver}>Добавить Водителя</button>
      </div>

      <div className="table-container">
        {Object.keys(groupedData).map((day) => (
          <div key={day}>
            <h2>Day {day}</h2>
            <table>
              <thead>
                <tr>
                  <th>Дата</th>
                  <th>Марка</th>
                  <th>Гос №</th>
                  <th>Водитель</th>
                  <th>Заказчик</th>
                  <th>№ Путевого</th>
                  <th>Часы</th>
                  <th>Потверждение</th>
                  <th>Стоимость Заказа</th>
                  <th>Оплата</th>
                  <th>Сумма Оплаты</th>
                  <th>Комментарий</th>
                  <th>Менаджер</th>
                  <th>Последнее редактирования</th>
                  <th>Удалить запись</th>
                </tr>
              </thead>
              <tbody>
                {groupedData[day].map((item, index) => (
                  <tr
                    key={item._id}
                    ref={index === 0 ? firstDayRef : null} // Присваиваем ref только первому элементу
                    className={item.colorClass}
                    style={{
                      backgroundColor: isToday(item.date)
                        ? "#f0f0f0"
                        : "transparent",
                    }}
                  >
                    <td>
                      <input
                        type="date"
                        value={formatDate(item.date) || ""}
                        onChange={(e) => handleDateChange(e, item._id)}
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        value={item.brand || ""}
                        onChange={(e) =>
                          handleInputChange(e, item._id, "brand")
                        }
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        value={item.vehicleNumber || ""}
                        onChange={(e) =>
                          handleInputChange(e, item._id, "vehicleNumber")
                        }
                      />
                    </td>
                    <td>
                      <select
                        value={item.driver || ""}
                        onChange={(e) =>
                          handleInputChange(e, item._id, "driver")
                        }
                      >
                        <option value="">Выбрать</option>
                        {drivers.map((driver) => (
                          <option key={driver._id} value={driver.name}>
                            {driver.name}
                          </option>
                        ))}
                      </select>
                    </td>
                    <td>
                      <textarea
                        style={{ resize: "none" }}
                        type="text"
                        value={item.customer || ""}
                        onChange={(e) =>
                          handleInputChange(e, item._id, "customer")
                        }
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        value={item.routeNumber || ""}
                        onChange={(e) =>
                          handleInputChange(e, item._id, "routeNumber")
                        }
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        value={item.hours || ""}
                        onChange={(e) =>
                          handleInputChange(e, item._id, "hours")
                        }
                      />
                    </td>
                    <td
                      style={{
                        display: "flex",
                        justifyContent: "center",
                        height: "47px",
                      }}
                    >
                      <input
                        type="checkbox"
                        checked={item.isTrue || false}
                        onChange={(e) =>
                          handleCheckboxChange(e, item._id, "checkbox")
                        }
                      />
                    </td>
                    <td>
                      <input
                        type="number"
                        value={item.price || ""}
                        onChange={(e) =>
                          handleInputChange(e, item._id, "price")
                        }
                      />
                    </td>
                    <td>
                      <select
                        value={item.colorData || ""}
                        onChange={(e) =>
                          handleInputChange(e, item._id, "colorData")
                        }
                        style={{
                          color: getBackColor(item.colorData),
                          backgroundColor: getColor(item.colorData),
                          fontWeight: "bold",
                        }} // Используйте item.colorData здесь
                      >
                        <option value="">Выбрать</option>
                        <option
                          value="Оплачен"
                          style={{ color: "green", fontWeight: "bold" }}
                        >
                          Оплачен &minus; &gt;
                        </option>
                        <option
                          value="Частично Оплачен"
                          style={{ color: "gold", fontWeight: "bold" }}
                        >
                          Частично Оплачен &minus; &gt;
                        </option>
                        <option
                          value="Нет Оплаты"
                          style={{ color: "red", fontWeight: "bold" }}
                        >
                          Нет Оплаты &minus; &gt;
                        </option>
                        <option
                          value="Отсрочка"
                          style={{ color: "yellow", fontWeight: "bold" }}
                        >
                          Отсрочка &minus; &gt;
                        </option>
                      </select>
                    </td>
                    <td>
                      <input
                        type="text"
                        value={item.calcPay || ""}
                        onChange={(e) =>
                          handleInputChange(e, item._id, "calcPay")
                        }
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        value={item.comment || ""}
                        onChange={(e) =>
                          handleInputChange(e, item._id, "comment")
                        }
                      />
                    </td>
                    <td>
                      <select
                        value={item.user || ""}
                        onChange={(e) => handleInputChange(e, item._id, "user")}
                      >
                        <option value="">Выбрать</option>
                        {users.map((user) => (
                          <option key={user._id} value={user.username}>
                            {user.username}
                          </option>
                        ))}
                      </select>
                    </td>
                    <td>
                      <input
                        disabled
                        type="text"
                        value={item.updatedBy || ""}
                        onChange={(e) =>
                          handleInputChange(e, item._id, "updatedBy")
                        }
                      />
                    </td>
                    <td>
                      <button
                        onClick={() => handleDeleteWithConfirmation(item._id)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
