// Home.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const formatDate = (date) => {
  const d = new Date(date);
  if (isNaN(d.getTime())) return "Invalid Date"; // Возвращаем строку для неверных дат
  return d.toLocaleDateString(); // Возвращаем дату в локальном формате
};

const Home = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  return (
    <div>
      <h1>Data Table</h1>
      <table>
        <thead>
          <tr>
            <th>Дата</th>
            <th>Марка</th>
            <th>ВОДИТЕЛЬ</th>
            <th>ЗАКАЗЧИК</th>
            <th>№ пут</th>
            <th>ЧАСЫ</th>
            <th>FALSE/true</th>
            <th>Гос №</th>
            <th>ЦЕНА</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td>{formatDate(item.date)}</td>
              <td>{item.brand}</td>
              <td>{item.driver}</td>
              <td>{item.customer}</td>
              <td>{item.routeNumber}</td>
              <td>{item.hours}</td>
              <td>{item.status ? "TRUE" : "FALSE"}</td>
              <td>{item.licensePlate}</td>
              <td>{item.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <a href="/add">Add New Data</a>
    </div>
  );
};

export default Home;
