import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        console.log(response.data); // Для проверки формата данных
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  // Функция для преобразования даты
  const formatDate = (date) => {
    const d = new Date(date);
    return isNaN(d.getTime())
      ? ""
      : d.toLocaleDateString("ru-RU", {
          month: "long",
          day: "numeric",
          year: "numeric",
        });
  };

  return (
    <div>
      <h1>Data Table</h1>
      <table>
        <thead>
          <tr>
            <th>Дата</th>
            <th>Марка</th>
            <th>ВОДИТЕЛЬ</th>
            <th>ЗАКАЗЧИК</th>
            <th>№ пут</th>
            <th>ЧАСЫ</th>
            <th>FALSE/true</th>
            <th>Гос №</th>
            <th>ЦЕНА</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <React.Fragment key={item._id}>
              <tr>
                <td>{formatDate(item.date)}</td>
                <td>{item.brand}</td>
                <td>{item.driver}</td>
                <td>{item.customer}</td>
                <td>{item.routeNumber}</td>
                <td>{item.hours}</td>
                <td>{item.isFalse ? "TRUE" : "FALSE"}</td>
                <td>{item.licensePlate}</td>
                <td>{item.price}</td>
              </tr>
            </React.Fragment>
          ))}
        </tbody>
      </table>
      <a href="/add">Add New Data</a>
    </div>
  );
};

export default Home;
