import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { hardcodedData } from "./hardcodedData";
import "./styles.css";
import { format, subMonths, startOfMonth, endOfMonth } from "date-fns";
import { ru } from "date-fns/locale";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [newDriver, setNewDriver] = useState("");
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split("T")[0],
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
    colorClass: "",
  });
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().split("T")[0]
  );
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const navigate = useNavigate();
  const currentDayRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/drivers");
        setDrivers(response.data);
      } catch (error) {
        console.error("Error fetching drivers:", error);
      }
    };

    fetchData();
    fetchDrivers();
  }, []);

  useEffect(() => {
    if (currentDayRef.current) {
      currentDayRef.current.scrollIntoView({
        behavior: "smooth",
        block: "start",
      });
    }
  }, [data]);

  const replaceSoftSign = (str) => {
    return str.replace(/ъ/g, "");
  };

  const sortedData = data.sort((a, b) => {
    if (a.colorClass === "highlight" && b.colorClass !== "highlight") return 1;
    if (a.colorClass !== "highlight" && b.colorClass === "highlight") return -1;

    const driverComparison = a.driver.localeCompare(b.driver);
    if (driverComparison !== 0) return driverComparison;

    return new Date(a.date) - new Date(b.date);
  });

  const groupByMonthAndDay = (data) => {
    return data.reduce((groups, item) => {
      const date = new Date(item.date);
      const monthYear = format(date, "MMMM yyyy", { locale: ru });
      const day = date.getDate();

      if (!groups[monthYear]) groups[monthYear] = {};
      if (!groups[monthYear][day]) groups[monthYear][day] = [];

      groups[monthYear][day].push(item);
      return groups;
    }, {});
  };

  const isToday = (itemDate) => {
    const today = new Date();
    const itemDay = new Date(itemDate);
    return (
      today.getDate() === itemDay.getDate() &&
      today.getMonth() === itemDay.getMonth() &&
      today.getFullYear() === itemDay.getFullYear()
    );
  };

  const getMonthName = (date) => {
    const monthNamesInGenitive = {
      январь: "января",
      февраль: "февраля",
      март: "марта",
      апрель: "апреля",
      май: "мая",
      июнь: "июня",
      июль: "июля",
      август: "августа",
      сентябрь: "сентября",
      октябрь: "октября",
      ноябрь: "ноября",
      декабрь: "декабря",
    };

    const monthName = format(date, "MMMM", { locale: ru });
    return monthNamesInGenitive[monthName] || monthName;
  };

  const handlePrevMonth = () => {
    setCurrentMonth(subMonths(currentMonth, 1));
  };

  const handleNextMonth = () => {
    setCurrentMonth(addMonths(currentMonth, 1));
  };

  const currentMonthData = sortedData.filter((item) => {
    const itemDate = new Date(item.date);
    const startOfMonthDate = startOfMonth(currentMonth);
    const endOfMonthDate = endOfMonth(currentMonth);
    return itemDate >= startOfMonthDate && itemDate <= endOfMonthDate;
  });

  return (
    <div>
      <div className="header">
        <h1>Data Table</h1>
        <div className="buttons">
          <button onClick={addEntriesForSelectedDate}>
            Add 37 Entries for Selected Date
          </button>
          <button onClick={addNewItem}>Add New Item</button>
          <button onClick={() => addNewItemWithClass("highlight")}>
            Add New Item with Highlight
          </button>
        </div>
      </div>
      <div className="controls">
        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
        />
        <input
          type="text"
          value={newDriver}
          onChange={(e) => setNewDriver(e.target.value)}
          placeholder="New Driver"
        />
        <button onClick={handleAddNewDriver}>Add New Driver</button>
      </div>
      <div className="month-controls">
        <button onClick={handlePrevMonth}>Previous Month</button>
        <h2>{getMonthName(currentMonth)}</h2>
        <button onClick={handleNextMonth}>Next Month</button>
      </div>
      <div className="data">
        {Object.entries(groupByMonthAndDay(currentMonthData)).map(
          ([month, days]) => (
            <div key={month}>
              <h2>{replaceSoftSign(month)}</h2>
              {Object.entries(days).map(([day, items]) => (
                <div
                  key={day}
                  ref={isToday(items[0].date) ? currentDayRef : null}
                >
                  <h3 className={isToday(items[0].date) ? "today-class" : ""}>
                    {day}{" "}
                    {replaceSoftSign(getMonthName(new Date(items[0].date)))}
                  </h3>
                  <table>
                    <thead>
                      <tr>
                        <th>Date</th>
                        <th>Brand</th>
                        <th>Driver</th>
                        <th>Customer</th>
                        <th>Route Number</th>
                        <th>Hours</th>
                        <th>True/False</th>
                        <th>Vehicle Number</th>
                        <th>Price</th>
                        <th>Last Edited By</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {items.map((item) => (
                        <tr key={item._id} className={item.colorClass || ""}>
                          <td>
                            <input
                              type="date"
                              value={
                                new Date(item.date).toISOString().split("T")[0]
                              }
                              onChange={(e) => handleDateChange(e, item._id)}
                            />
                          </td>
                          <td>
                            <input
                              type="text"
                              value={item.brand || ""}
                              onChange={(e) =>
                                handleInputChange(e, item._id, "brand")
                              }
                            />
                          </td>
                          <td>
                            <select
                              value={item.driver || ""}
                              onChange={(e) =>
                                handleInputChange(e, item._id, "driver")
                              }
                            >
                              <option value="">Select Driver</option>
                              {drivers.map((driver) => (
                                <option key={driver._id} value={driver._id}>
                                  {driver.name}
                                </option>
                              ))}
                            </select>
                          </td>
                          <td>
                            <input
                              type="text"
                              value={item.customer || ""}
                              onChange={(e) =>
                                handleInputChange(e, item._id, "customer")
                              }
                            />
                          </td>
                          <td>
                            <input
                              type="text"
                              value={item.routeNumber || ""}
                              onChange={(e) =>
                                handleInputChange(e, item._id, "routeNumber")
                              }
                            />
                          </td>
                          <td>
                            <input
                              type="number"
                              value={item.hours || ""}
                              onChange={(e) =>
                                handleInputChange(e, item._id, "hours")
                              }
                            />
                          </td>
                          <td>
                            <input
                              type="checkbox"
                              checked={item.isTrue || false}
                              onChange={(e) =>
                                handleCheckboxChange(e, item._id)
                              }
                            />
                          </td>
                          <td>
                            <input
                              type="text"
                              value={item.vehicleNumber || ""}
                              onChange={(e) =>
                                handleInputChange(e, item._id, "vehicleNumber")
                              }
                            />
                          </td>
                          <td>
                            <input
                              type="number"
                              value={item.price || ""}
                              onChange={(e) =>
                                handleInputChange(e, item._id, "price")
                              }
                            />
                          </td>
                          <td>{item.updatedBy || "N/A"}</td>
                          <td>
                            <button onClick={() => handleDeleteItem(item._id)}>
                              Delete
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              ))}
            </div>
          )
        )}
      </div>
    </div>
  );
};

export default Home;
