import React from "react";
import { useParams, useNavigate } from "react-router-dom";

const MonthPage = ({
  data,
  drivers,
  handleInputChange,
  handleDateChange,
  handleCheckboxChange,
  handleDeleteWithConfirmation,
  getMonthName,
  replaceSoftSign,
  isToday,
  currentDayRef,
}) => {
  const { month } = useParams(); // Получаем месяц из параметров URL
  const navigate = useNavigate();

  // Фильтруем данные по месяцу
  const filteredData = data.filter((item) => {
    const itemDate = new Date(item.date);
    const monthName = itemDate.toLocaleString("default", { month: "long" });
    return replaceSoftSign(monthName) === replaceSoftSign(month);
  });

  const groupByDay = (filteredData) => {
    return filteredData.reduce((groups, item) => {
      const day = new Date(item.date).getDate();
      if (!groups[day]) groups[day] = [];
      groups[day].push(item);
      return groups;
    }, {});
  };

  const groupedByDay = groupByDay(filteredData);

  return (
    <div>
      <h2>{replaceSoftSign(month)}</h2>
      <button onClick={() => navigate("/")}>Go Back</button>
      {Object.entries(groupedByDay).map(([day, items]) => (
        <div key={day} ref={isToday(items[0].date) ? currentDayRef : null}>
          <h3 className={isToday(items[0].date) ? "today-class" : ""}>
            {day} {replaceSoftSign(getMonthName(new Date(items[0].date)))}
          </h3>
          <table>
            <thead>
              <tr>
                <th>Date</th>
                <th>Brand</th>
                <th>Driver</th>
                <th>Customer</th>
                <th>Route Number</th>
                <th>Hours</th>
                <th>True/False</th>
                <th>Vehicle Number</th>
                <th>Price</th>
                <th>Last Edited By</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {items.map((item) => (
                <tr key={item._id} className={item.colorClass || ""}>
                  <td>
                    <input
                      type="date"
                      value={new Date(item.date).toISOString().split("T")[0]}
                      onChange={(e) => handleDateChange(e, item._id)}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.brand || ""}
                      onChange={(e) => handleInputChange(e, item._id, "brand")}
                    />
                  </td>
                  <td>
                    <select
                      value={item.driver || ""}
                      onChange={(e) => handleInputChange(e, item._id, "driver")}
                    >
                      <option value="">Select Driver</option>
                      {drivers.map((driver) => (
                        <option key={driver._id} value={driver._id}>
                          {driver.name}
                        </option>
                      ))}
                    </select>
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.customer || ""}
                      onChange={(e) =>
                        handleInputChange(e, item._id, "customer")
                      }
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.routeNumber || ""}
                      onChange={(e) =>
                        handleInputChange(e, item._id, "routeNumber")
                      }
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      value={item.hours || ""}
                      onChange={(e) => handleInputChange(e, item._id, "hours")}
                    />
                  </td>
                  <td>
                    <input
                      type="checkbox"
                      checked={item.isTrue || false}
                      onChange={(e) => handleCheckboxChange(e, item._id)}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.vehicleNumber || ""}
                      onChange={(e) =>
                        handleInputChange(e, item._id, "vehicleNumber")
                      }
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.price || ""}
                      onChange={(e) => handleInputChange(e, item._id, "price")}
                    />
                  </td>
                  <td>
                    <span>{item.updatedBy || "N/A"}</span>
                  </td>
                  <td>
                    <button
                      onClick={() => handleDeleteWithConfirmation(item._id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

export default MonthPage;
