import React, { useState, useEffect } from "react";
import axios from "axios";
import "./styles.css"; // Подключение стилей

const Home = () => {
  const [data, setData] = useState([]);
  const [editingItem, setEditingItem] = useState(null);
  const [form, setForm] = useState({
    date: "",
    brand: "",
    driver: "",
    client: "",
    routeNumber: "",
    hours: "",
    isTrue: "",
    licensePlate: "",
    price: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  const handleAddItem = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", form);
      setData([...data, response.data]);
      setForm({
        date: "",
        brand: "",
        driver: "",
        client: "",
        routeNumber: "",
        hours: "",
        isTrue: "",
        licensePlate: "",
        price: "",
      });
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  const handleEditItem = async (id) => {
    try {
      const response = await axios.put(
        `http://localhost:5000/api/data/${id}`,
        form
      );
      setData(data.map((item) => (item._id === id ? response.data : item)));
      setEditingItem(null);
      setForm({
        date: "",
        brand: "",
        driver: "",
        client: "",
        routeNumber: "",
        hours: "",
        isTrue: "",
        licensePlate: "",
        price: "",
      });
    } catch (error) {
      console.error("Error editing data:", error);
    }
  };

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div>
      <h1>Data Table</h1>
      <div className="table-container">
        <table className="table">
          <thead>
            <tr>
              <th>Дата</th>
              <th>Марка</th>
              <th>ВОДИТЕЛЬ</th>
              <th>ЗАКАЗЧИК</th>
              <th>№ пут</th>
              <th>ЧАСЫ</th>
              <th>FALSE/true</th>
              <th>Гос №</th>
              <th>ЦЕНА</th>
              <th>Действия</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item) => (
              <tr key={item._id}>
                <td>{item.date}</td>
                <td>{item.brand}</td>
                <td>{item.driver}</td>
                <td>{item.client}</td>
                <td>{item.routeNumber}</td>
                <td>{item.hours}</td>
                <td>{item.isTrue}</td>
                <td>{item.licensePlate}</td>
                <td>{item.price}</td>
                <td>
                  <button onClick={() => setEditingItem(item)}>
                    Редактировать
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {editingItem && (
        <div>
          <h2>Редактировать данные</h2>
          <input
            type="text"
            name="date"
            value={form.date}
            onChange={handleChange}
            placeholder="Дата"
          />
          <input
            type="text"
            name="brand"
            value={form.brand}
            onChange={handleChange}
            placeholder="Марка"
          />
          <input
            type="text"
            name="driver"
            value={form.driver}
            onChange={handleChange}
            placeholder="ВОДИТЕЛЬ"
          />
          <input
            type="text"
            name="client"
            value={form.client}
            onChange={handleChange}
            placeholder="ЗАКАЗЧИК"
          />
          <input
            type="text"
            name="routeNumber"
            value={form.routeNumber}
            onChange={handleChange}
            placeholder="№ пут"
          />
          <input
            type="text"
            name="hours"
            value={form.hours}
            onChange={handleChange}
            placeholder="ЧАСЫ"
          />
          <input
            type="text"
            name="isTrue"
            value={form.isTrue}
            onChange={handleChange}
            placeholder="FALSE/true"
          />
          <input
            type="text"
            name="licensePlate"
            value={form.licensePlate}
            onChange={handleChange}
            placeholder="Гос №"
          />
          <input
            type="text"
            name="price"
            value={form.price}
            onChange={handleChange}
            placeholder="ЦЕНА"
          />
          <button onClick={() => handleEditItem(editingItem._id)}>
            Сохранить изменения
          </button>
        </div>
      )}
      <h2>Добавить данные</h2>
      <input
        type="text"
        name="date"
        value={form.date}
        onChange={handleChange}
        placeholder="Дата"
      />
      <input
        type="text"
        name="brand"
        value={form.brand}
        onChange={handleChange}
        placeholder="Марка"
      />
      <input
        type="text"
        name="driver"
        value={form.driver}
        onChange={handleChange}
        placeholder="ВОДИТЕЛЬ"
      />
      <input
        type="text"
        name="client"
        value={form.client}
        onChange={handleChange}
        placeholder="ЗАКАЗЧИК"
      />
      <input
        type="text"
        name="routeNumber"
        value={form.routeNumber}
        onChange={handleChange}
        placeholder="№ пут"
      />
      <input
        type="text"
        name="hours"
        value={form.hours}
        onChange={handleChange}
        placeholder="ЧАСЫ"
      />
      <input
        type="text"
        name="isTrue"
        value={form.isTrue}
        onChange={handleChange}
        placeholder="FALSE/true"
      />
      <input
        type="text"
        name="licensePlate"
        value={form.licensePlate}
        onChange={handleChange}
        placeholder="Гос №"
      />
      <input
        type="text"
        name="price"
        value={form.price}
        onChange={handleChange}
        placeholder="ЦЕНА"
      />
      <button onClick={handleAddItem}>Добавить данные</button>
    </div>
  );
};

export default Home;
