import React, { useState, useEffect } from "react";
import axios from "axios";
import { format, startOfMonth, endOfMonth, eachDayOfInterval } from "date-fns";

const MonthlyData = ({ month, data, drivers }) => {
  const [monthlyData, setMonthlyData] = useState([]);
  const [currentDate, setCurrentDate] = useState(new Date());
  const [yearMonth, setYearMonth] = useState(format(new Date(), "yyyy-MM"));

  useEffect(() => {
    const fetchMonthlyData = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5000/api/data?yearMonth=${yearMonth}`
        );
        setMonthlyData(response.data);
      } catch (error) {
        console.error("Error fetching monthly data:", error);
      }
    };

    fetchMonthlyData();
  }, [yearMonth]);

  const handleChangeMonth = (offset) => {
    const newDate = new Date(currentDate);
    newDate.setMonth(currentDate.getMonth() + offset);
    setCurrentDate(newDate);
    setYearMonth(format(newDate, "yyyy-MM"));
  };

  const monthStart = startOfMonth(currentDate);
  const monthEnd = endOfMonth(currentDate);
  const monthDays = eachDayOfInterval({ start: monthStart, end: monthEnd });

  return (
    <div>
      <div className="month-navigation">
        <button onClick={() => handleChangeMonth(-1)}>Previous Month</button>
        <h2>{format(currentDate, "MMMM yyyy")}</h2>
        <button onClick={() => handleChangeMonth(1)}>Next Month</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Brand</th>
            <th>Driver</th>
            <th>Customer</th>
            <th>Route Number</th>
            <th>Hours</th>
            <th>Vehicle Number</th>
            <th>Price</th>
            <th>Color Class</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {monthlyData.map((item) => (
            <tr key={item._id}>
              <td>{format(new Date(item.date), "dd MMMM yyyy")}</td>
              <td>{item.brand}</td>
              <td>{item.driver}</td>
              <td>{item.customer}</td>
              <td>{item.routeNumber}</td>
              <td>{item.hours}</td>
              <td>{item.vehicleNumber}</td>
              <td>{item.price}</td>
              <td>{item.colorClass}</td>
              <td>
                <button onClick={() => console.log("Edit", item._id)}>
                  Edit
                </button>
                <button onClick={() => console.log("Delete", item._id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MonthlyData;
