import React, { useState, useEffect } from "react";
import Confetti from "react-confetti"; // Импортируем конфетти
import "./newYearPopup.css"; // Путь к стилям

const NewYearPopup = () => {
  const [showPopup, setShowPopup] = useState(true);
  const [confetti, setConfetti] = useState(false);
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const [windowHeight, setWindowHeight] = useState(window.innerHeight);

  const handleClick = () => {
    setConfetti(true);
    setTimeout(() => {
      setShowPopup(false); // Закрываем попап через 3 секунды
    }, 3000); // Попап скрывается после 3 секунд
  };

  // Обновляем размеры окна при изменении размера экрана
  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
      setWindowHeight(window.innerHeight);
    };

    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  // Используем setTimeout для удаления конфетти после 3 минут
  useEffect(() => {
    if (confetti) {
      const confettiTimer = setTimeout(() => {
        setConfetti(false); // Останавливаем конфетти через 3 минуты
      }, 180000); // Конфетти исчезает через 3 минуты
      return () => clearTimeout(confettiTimer);
    }
  }, [confetti]);

  return (
    <>
      {/* Конфетти всегда показываются, если они активированы */}
      {confetti && (
        <Confetti
          width={windowWidth} // Устанавливаем ширину
          height={windowHeight} // Устанавливаем высоту
        />
      )}

      {/* Показываем попап только если он должен быть видим */}
      {showPopup && (
        <div className="popup-overlay">
          <div className="popup-content">
            <img
              className="popup-img"
              src="https://example.com/your-image.jpg" // Замените на реальный URL картинки
              alt="С Новым годом!"
            />
            <h2>С Новым годом!</h2>
            <p>Пусть этот год принесет счастье и радость!</p>

            <div className="icon-container">
              <i className="fas fa-snowflake"></i>{" "}
            </div>

            <button className="popup-button" onClick={handleClick}>
              Нажми на меня
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default NewYearPopup;
