import React, { useState, useEffect } from "react";
import axios from "axios";

const Profile = () => {
  const [items, setItems] = useState([]);
  const [user, setUser] = useState(null); // Информация о пользователе

  // Получение данных пользователя и созданных им элементов
  useEffect(() => {
    const fetchData = async () => {
      try {
        const username = localStorage.getItem("username");
        setUser(username);

        const response = await axios.get("http://localhost:5000/api/data");
        const userItems = response.data.filter(
          (item) => item.createdBy === username
        );
        setItems(userItems);
      } catch (error) {
        console.error("Ошибка при загрузке данных:", error);
      }
    };

    fetchData();
  }, []);

  if (!user) {
    return <p>Загрузка...</p>;
  }

  // Вычисляем сумму всех значений price
  const totalPrice = items.reduce((total, item) => {
    return total + (item.price || 0); // Если price отсутствует, считаем его как 0
  }, 0);

  return (
    <div>
      <p>Количество созданных элементов: {items.length}</p>
      <p>Общее количество сумм заказов: {totalPrice}</p>
    </div>
  );
};

export default Profile;
