import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './styles.css'; // Подключаем стили

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [newDriver, setNewDriver] = useState('');
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split('T')[0],
    brand: '',
    driver: '',
    customer: '',
    routeNumber: '',
    hours: '',
    isTrue: false,
    vehicleNumber: '',
    price: '',
    colorClass: '',
  });
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const currentDayRef = useRef(null);
  const [activeInput, setActiveInput] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/data');
        setData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/drivers');
        setDrivers(response.data);
      } catch (error) {
        console.error('Error fetching drivers:', error);
      }
    };

    fetchData();
    fetchDrivers();
  }, []);

  useEffect(() => {
    if (currentDayRef.current) {
      currentDayRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }, [data]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    // Добавьте навигацию на страницу логина, если используется React Router
    window.location.href = '/login';
  };

  const addEntriesForSelectedDate = async () => {
    try {
      const newEntries = Array.from({ length: 37 }).map((_, index) => ({
        date: selectedDate,
        brand: `Brand ${index + 1}`,
        driver: drivers.length > 0 ? drivers[0].name : '',
        customer: `Customer ${index + 1}`,
        routeNumber: `Route ${index + 1}`,
        hours: `${index + 1}`,
        isTrue: false,
        vehicleNumber: `Vehicle ${index + 1}`,
        price: `${index + 1}`,
        colorClass: index % 2 === 0 ? 'highlight' : '',
      }));

      await Promise.all(newEntries.map((entry) => axios.post('http://localhost:5000/api/data', entry)));

      const response = await axios.get('http://localhost:5000/api/data');
      setData(response.data);
    } catch (error) {
      console.error('Error adding entries:', error);
    }
  };

  const addNewItem = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/data', newItem);
      setData([...data, response.data]);
      setNewItem({
        date: new Date().toISOString().split('T')[0],
        brand: '',
        driver: '',
        customer: '',
        routeNumber: '',
        hours: '',
        isTrue: false,
        vehicleNumber: '',
        price: '',
        colorClass: '',
      });
    } catch (error) {
      console.error('Error adding new item:', error);
    }
  };

  const handleInputChange = (e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === 'checkbox' ? checked : value;

    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
    );
    setData(updatedData);

    axios.put(`http://localhost:5000/api/data/${itemId}`, {
      ...updatedData.find((item) => item._id === itemId),
      [fieldName]: updatedValue,
    }).catch((error) => console.error('Error updating data:', error));
  };

  const handleInputFocus = (e, itemId, fieldName) => {
    if (fieldName === 'brand') {
      const { value } = e.target;
      const currentItem = data.find((item) => item._id === itemId);

      if (currentItem && currentItem[fieldName] && value !== currentItem[fieldName]) {
        const confirmChange = window.confirm('Вы уверены, что хотите изменить значение бренда?');
        if (!confirmChange) {
          e.target.blur();
          return;
        }
      }
    }
    setActiveInput({ itemId, fieldName });
  };

  const handleDateChange = (e, itemId) => {
    const { value } = e.target;

    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, date: value } : item
    );
    setData(updatedData);

    axios.put(`http://localhost:5000/api/data/${itemId}`, {
      ...updatedData.find((item) => item._id === itemId),
      date: value,
    }).catch((error) => console.error('Error updating date:', error));
  };

  const handleCheckboxChange = async (e, itemId) => {
    const { checked } = e.target;

    const isConfirmed = window.confirm('Вы уверены, что хотите изменить состояние этого чекбокса?');
    if (!isConfirmed) {
      return;
    }

    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, isTrue: checked } : item
    );
    setData(updatedData);

    try {
      await axios.put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        isTrue: checked,
      });
    } catch (error) {
      console.error('Error updating data:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/data/${id}`);
      setData(data.filter(item => item._id !== id));
    } catch (error) {
      console.error('Error deleting data:', error);
    }
  };

  const handleDeleteWithConfirmation = (id) => {
    const isConfirmed = window.confirm('Вы уверены, что хотите удалить этот элемент?');
    if (isConfirmed) handleDelete(id);
  };

  const handleAddNewDriver = async () => {
    if (newDriver.trim() === '') return;

    try {
      const response = await axios.post('http://localhost:5000/api/drivers', {
        name: newDriver,
      });
      setDrivers([...drivers, response.data]);
      setNewDriver('');
    } catch (error) {
      console.error('Error adding driver:', error);
    }
  };

  const groupByMonthAndDay = (data) => {
    return data.reduce((groups, item) => {
      const date = new Date(item.date);
      const monthYear = `${date.toLocaleString('default', { month: 'long' })} ${date.getFullYear()}`;
      const day = date.getDate();

      if (!groups[monthYear]) groups[monthYear] = {};
      if (!groups[monthYear][day]) groups[monthYear][day] = [];

      groups[monthYear][day].push(item);
      return groups;
    }, {});
  };

  const isToday = (itemDate) => {
    const today = new Date();
    const itemDay = new Date(itemDate);
    return (
      today.getDate() === itemDay.getDate() &&
      today.getMonth() === itemDay.getMonth() &&
      today.getFullYear() === itemDay.getFullYear()
    );
  };

  return (
    <div>
      <div className="header">
        <h1>Data Table</h1>
        <div className="buttons">
          <button onClick={addEntriesForSelectedDate}>Add 37 Entries for Selected Date</button>
          <button onClick={addNewItem}>Add New Item</button>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </div>
      <div className="controls">
        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
        />
        <input
          type="text"
          value={newDriver}
          onChange={(e) => setNewDriver(e.target.value)}
          placeholder="New Driver Name"
        />
        <button onClick={handleAddNewDriver}>Add New Driver</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Brand</th>
            <th>Driver</th>
            <th>Customer</th>
            <th>Route Number</th>
            <th>Hours</th>
            <th>Is True</th>
            <th>Vehicle Number</th>
            <th>Price</th>
            <th>Color</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(groupByMonthAndDay(data)).map(([month, days]) => (
            <React.Fragment key={month}>
              <tr>
                <td colSpan="11" className="month-row">
                  {month}
                </td>
              </tr>
              {Object.entries(days).map(([day, items]) => (
                <React.Fragment key={day}>
                  <tr>
                    <td
                      ref={isToday(new Date(`${month} ${day}`)) ? currentDayRef : null}
                      colSpan="11"
                      className="day-row"
                    >
                      {day}
                    </td>
                  </tr>
                  {items.map((item) => (
                    <tr key={item._id}>
                      <td>
                        <input
                          type="date"
                          value={item.date}
                          onChange={(e) => handleDateChange(e, item._id)}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.brand}
                          onChange={(e) => handleInputChange(e, item._id, 'brand')}
                          onFocus={(e) => handleInputFocus(e, item._id, 'brand')}
                        />
                      </td>
                      <td>
                        <select
                          value={item.driver}
                          onChange={(e) => handleInputChange(e, item._id, 'driver')}
                        >
                          <option value="">Select Driver</option>
                          {drivers.map((driver) => (
                            <option key={driver._id} value={driver.name}>
                              {driver.name}
                            </option>
                          ))}
                        </select>
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.customer}
                          onChange={(e) => handleInputChange(e, item._id, 'customer')}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.routeNumber}
                          onChange={(e) => handleInputChange(e, item._id, 'routeNumber')}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.hours}
                          onChange={(e) => handleInputChange(e, item._id, 'hours')}
                        />
                      </td>
                      <td>
                        <input
                          type="checkbox"
                          checked={item.isTrue}
                          onChange={(e) => handleCheckboxChange(e, item._id)}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.vehicleNumber}
                          onChange={(e) => handleInputChange(e, item._id, 'vehicleNumber')}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.price}
                          onChange={(e) => handleInputChange(e, item._id, 'price')}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.colorClass}
                          onChange={(e) => handleInputChange(e, item._id, 'colorClass')}
                        />
                      </td>
                      <td>
                        <button onClick={() => handleDeleteWithConfirmation(item._id)}>
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </React.Fragment>
              ))}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
