import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { hardcodedData } from "./hardcodedData";
import "./styles.css";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [newDriver, setNewDriver] = useState("");
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split("T")[0],
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
    colorClass: "",
  });
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().split("T")[0]
  );
  const [tooltipData, setTooltipData] = useState(null);
  const [tooltipPosition, setTooltipPosition] = useState({ left: 0, top: 0 });
  const [tooltipVisible, setTooltipVisible] = useState(false);
  const navigate = useNavigate();
  const currentDayRef = useRef(null);
  const inputRefs = useRef({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/drivers");
        setDrivers(response.data);
      } catch (error) {
        console.error("Error fetching drivers:", error);
      }
    };

    fetchData();
    fetchDrivers();
  }, []);

  useEffect(() => {
    if (currentDayRef.current) {
      currentDayRef.current.scrollIntoView({ behavior: "smooth", block: "start" });
    }
  }, [data]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const replaceSoftSign = (str) => {
    return str.replace(/ъ/g, '');
  };

  const sortedData = data
    .sort((a, b) => {
      if (a.colorClass === 'highlight' && b.colorClass !== 'highlight') return 1;
      if (a.colorClass !== 'highlight' && b.colorClass === 'highlight') return -1;
      const driverComparison = a.driver.localeCompare(b.driver);
      if (driverComparison !== 0) return driverComparison;
      return new Date(a.date) - new Date(b.date);
    });

  const addEntriesForSelectedDate = async () => {
    try {
      const newEntries = hardcodedData.map((item) => ({
        ...item,
        date: selectedDate,
      }));

      await Promise.all(
        newEntries.map((entry) =>
          axios.post("http://localhost:5000/api/data", entry)
        )
      );

      const response = await axios.get("http://localhost:5000/api/data");
      setData(response.data);
    } catch (error) {
      console.error("Error adding entries:", error);
    }
  };

  const addNewItem = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", newItem);
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  const handleInputChange = (e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;

    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
    );
    setData(updatedData);

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        [fieldName]: updatedValue,
        updatedBy: localStorage.getItem("userName") // Замените на реальный метод получения имени пользователя
      })
      .catch((error) => console.error("Error saving data:", error));
  };

  const addNewItemWithClass = async (className) => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", {
        ...newItem,
        colorClass: className,
      });
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data with class:", error);
    }
  };

  const handleDeleteWithConfirmation = async (id) => {
    const isConfirmed = window.confirm("Are you sure you want to delete this item?");
    if (isConfirmed) {
      try {
        await axios.delete(`http://localhost:5000/api/data/${id}`);
        setData(data.filter(item => item._id !== id));
      } catch (error) {
        console.error("Error deleting data:", error);
      }
    }
  };

  const handleDateChange = (e, itemId) => {
    const { value } = e.target;

    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, date: value } : item
    );
    setData(updatedData);

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        date: value,
        updatedBy: localStorage.getItem("userName") // Замените на реальный метод получения имени пользователя
      })
      .catch((error) => console.error("Error updating date:", error));
  };

  const handleAddNewDriver = async () => {
    if (newDriver.trim() === "") return;

    try {
      const response = await axios.post("http://localhost:5000/api/drivers", {
        name: newDriver,
      });
      setDrivers([...drivers, response.data]);
      setNewDriver("");
    } catch (error) {
      console.error("Error adding driver:", error);
    }
  };

  const groupByMonthAndDay = (data) => {
    return data.reduce((groups, item) => {
      const date = new Date(item.date);
      const monthYear = date.toLocaleString("default", { month: "long" });
      const day = date.getDate();

      if (!groups[monthYear]) groups[monthYear] = {};
      if (!groups[monthYear][day]) groups[monthYear][day] = [];

      groups[monthYear][day].push(item);
      return groups;
    }, {});
  };

  const isToday = (itemDate) => {
    const today = new Date();
    const itemDay = new Date(itemDate);
    return (
      today.getDate() === itemDay.getDate() &&
      today.getMonth() === itemDay.getMonth() &&
      today.getFullYear() === itemDay.getFullYear()
    );
  };

  const getMonthName = (date) => {
    return new Intl.DateTimeFormat('ru-RU', { month: 'long' }).format(date);
  };

  const handleMouseEnter = (e, itemId, type) => {
    const rect = e.target.getBoundingClientRect();
    setTooltipData(data.find(item => item._id === itemId)?.updatedBy || "");
    setTooltipPosition({
      left: rect.left + window.scrollX,
      top: rect.bottom + window.scrollY + 5
    });
    setTooltipVisible(true);
  };

  const handleMouseLeave = () => {
    setTooltipVisible(false);
  };

  return (
    <div>
      <div className="header">
        <h1>Data Table</h1>
        <div className="buttons">
          <button onClick={addEntriesForSelectedDate}>
            Add 37 Entries for Selected Date
          </button>
          <button onClick={addNewItem}>Add New Item</button>
          <button onClick={() => addNewItemWithClass("highlight")}>
            Add New Item with Highlight
          </button>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </div>
      <div className="controls">
        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
        />
        <input
          type="text"
          value={newDriver}
          onChange={(e) => setNewDriver(e.target.value)}
          placeholder="New Driver"
        />
        <button onClick={handleAddNewDriver}>Add New Driver</button>
      </div>
      <div className="data">
        {Object.entries(groupByMonthAndDay(sortedData)).map(([month, days]) => (
          <div key={month}>
            <h2>{month}</h2>
            {Object.entries(days).map(([day, items]) => (
              <div
                key={day}
                ref={isToday(items[0].date) ? currentDayRef : null}
                className={isToday(items[0].date) ? "highlight" : ""}
              >
                <h3>{day}</h3>
                <table>
                  <thead>
                    <tr>
                      <th>Date</th>
                      <th>Brand</th>
                      <th>Driver</th>
                      <th>Customer</th>
                      <th>Route Number</th>
                      <th>Hours</th>
                      <th>Is True</th>
                      <th>Vehicle Number</th>
                      <th>Price</th>
                      <th>Color Class</th>
                      <th>Updated By</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {items.map((item) => (
                      <tr key={item._id}>
                        <td>
                          <input
                            type="date"
                            value={item.date.split("T")[0]}
                            onChange={(e) => handleDateChange(e, item._id)}
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.brand}
                            onChange={(e) => handleInputChange(e, item._id, "brand")}
                            onMouseEnter={(e) => handleMouseEnter(e, item._id, "brand")}
                            onMouseLeave={handleMouseLeave}
                            ref={(el) => (inputRefs.current[item._id] = el)}
                          />
                        </td>
                        <td>
                          <select
                            value={item.driver}
                            onChange={(e) => handleInputChange(e, item._id, "driver")}
                            onMouseEnter={(e) => handleMouseEnter(e, item._id, "driver")}
                            onMouseLeave={handleMouseLeave}
                          >
                            <option value="">Select Driver</option>
                            {drivers.map((driver) => (
                              <option key={driver._id} value={driver.name}>
                                {driver.name}
                              </option>
                            ))}
                          </select>
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.customer}
                            onChange={(e) => handleInputChange(e, item._id, "customer")}
                            onMouseEnter={(e) => handleMouseEnter(e, item._id, "customer")}
                            onMouseLeave={handleMouseLeave}
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.routeNumber}
                            onChange={(e) => handleInputChange(e, item._id, "routeNumber")}
                            onMouseEnter={(e) => handleMouseEnter(e, item._id, "routeNumber")}
                            onMouseLeave={handleMouseLeave}
                          />
                        </td>
                        <td>
                          <input
                            type="number"
                            value={item.hours}
                            onChange={(e) => handleInputChange(e, item._id, "hours")}
                            onMouseEnter={(e) => handleMouseEnter(e, item._id, "hours")}
                            onMouseLeave={handleMouseLeave}
                          />
                        </td>
                        <td>
                          <input
                            type="checkbox"
                            checked={item.isTrue}
                            onChange={(e) => handleInputChange(e, item._id, "isTrue")}
                            onMouseEnter={(e) => handleMouseEnter(e, item._id, "isTrue")}
                            onMouseLeave={handleMouseLeave}
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.vehicleNumber}
                            onChange={(e) => handleInputChange(e, item._id, "vehicleNumber")}
                            onMouseEnter={(e) => handleMouseEnter(e, item._id, "vehicleNumber")}
                            onMouseLeave={handleMouseLeave}
                          />
                        </td>
                        <td>
                          <input
                            type="number"
                            value={item.price}
                            onChange={(e) => handleInputChange(e, item._id, "price")}
                            onMouseEnter={(e) => handleMouseEnter(e, item._id, "price")}
                            onMouseLeave={handleMouseLeave}
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.colorClass}
                            onChange={(e) => handleInputChange(e, item._id, "colorClass")}
                            onMouseEnter={(e) => handleMouseEnter(e, item._id, "colorClass")}
                            onMouseLeave={handleMouseLeave}
                          />
                        </td>
                        <td>{item.updatedBy}</td>
                        <td>
                          <button onClick={() => handleDeleteWithConfirmation(item._id)}>
                            Delete
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ))}
          </div>
        ))}
      </div>
      {tooltipVisible && (
        <div
          className="tooltip"
          style={{ left: tooltipPosition.left, top: tooltipPosition.top }}
        >
          {tooltipData}
        </div>
      )}
    </div>
  );
};

export default Home;
