import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [newDriver, setNewDriver] = useState("");
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split("T")[0], // Текущая дата по умолчанию
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
  });

  // Получение данных с сервера
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/drivers");
        setDrivers(response.data);
      } catch (error) {
        console.error("Error fetching drivers:", error);
      }
    };

    fetchData();
    fetchDrivers();
  }, []);

  // Сортировка данных по дате
  const sortedData = data.sort((a, b) => new Date(a.date) - new Date(b.date));

  const handleInputChange = (e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;

    // Обновляем локально
    setData(
      data.map((item) =>
        item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
      )
    );

    // Сохраняем изменения на сервере
    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...data.find((item) => item._id === itemId),
        [fieldName]: updatedValue,
      })
      .catch((error) => console.error("Error saving data:", error));
  };

  // Обработка изменения даты
  const handleDateChange = (e, itemId) => {
    const { value } = e.target;

    // Обновляем дату локально
    setData(
      data.map((item) =>
        item._id === itemId ? { ...item, date: value } : item
      )
    );

    // Сохраняем дату на сервере
    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...data.find((item) => item._id === itemId),
        date: value,
      })
      .catch((error) => console.error("Error updating date:", error));
  };

  const handleAddNewDriver = async () => {
    if (newDriver.trim() === "") return;

    try {
      const response = await axios.post("http://localhost:5000/api/drivers", {
        name: newDriver,
      });
      setDrivers([...drivers, response.data]);
      setNewDriver("");
    } catch (error) {
      console.error("Error adding driver:", error);
    }
  };

  // Добавление нового элемента
  const addNewItem = async () => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/data",
        newItem
      );
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  // Функция для группировки данных по месяцам
  const groupByMonth = (data) => {
    return data.reduce((groups, item) => {
      const month = new Date(item.date).toLocaleString("default", {
        month: "long",
        year: "numeric",
      });

      if (!groups[month]) {
        groups[month] = [];
      }
      groups[month].push(item);
      return groups;
    }, {});
  };

  return (
    <div>
      <h1>Data Table</h1>
      <button onClick={addNewItem}>Add New Item</button>

      <div>
        <input
          type="text"
          value={newDriver}
          onChange={(e) => setNewDriver(e.target.value)}
          placeholder="Enter new driver name"
        />
        <button onClick={handleAddNewDriver}>Add New Driver</button>
      </div>

      {Object.entries(groupByMonth(sortedData)).map(([month, items]) => (
        <div key={month}>
          <h2>{month}</h2>
          <table>
            <thead>
              <tr>
                <th>Date</th>
                <th>Brand</th>
                <th>Driver</th>
                <th>Customer</th>
                <th>Route Number</th>
                <th>Hours</th>
                <th>True/False</th>
                <th>Vehicle Number</th>
                <th>Price</th>
              </tr>
            </thead>
            <tbody>
              {items.map((item) => (
                <tr key={item._id}>
                  <td>
                    <input
                      type="date"
                      value={new Date(item.date).toISOString().split("T")[0]}
                      onChange={(e) => handleDateChange(e, item._id)}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.brand || ""}
                      onChange={(e) => handleInputChange(e, item._id, "brand")}
                    />
                  </td>
                  <td>
                    <select
                      value={item.driver || ""}
                      onChange={(e) => handleInputChange(e, item._id, "driver")}
                    >
                      <option value="">Select a driver</option>
                      {drivers.map((driver) => (
                        <option key={driver._id} value={driver._id}>
                          {driver.name}
                        </option>
                      ))}
                    </select>
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.customer || ""}
                      onChange={(e) =>
                        handleInputChange(e, item._id, "customer")
                      }
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.routeNumber || ""}
                      onChange={(e) =>
                        handleInputChange(e, item._id, "routeNumber")
                      }
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.hours || ""}
                      onChange={(e) => handleInputChange(e, item._id, "hours")}
                    />
                  </td>
                  <td>
                    <input
                      type="checkbox"
                      checked={item.isTrue || false}
                      onChange={(e) => handleInputChange(e, item._id, "isTrue")}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.vehicleNumber || ""}
                      onChange={(e) =>
                        handleInputChange(e, item._id, "vehicleNumber")
                      }
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={item.price || ""}
                      onChange={(e) => handleInputChange(e, item._id, "price")}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

export default Home;
