import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { hardcodedData } from "./hardcodedData"; // Убедитесь, что путь правильный
import "./styles.css"; // Импорт стилей для кнопок
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [newDriver, setNewDriver] = useState("");
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split("T")[0],
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
    colorClass: "", // Добавлен colorClass
  });
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().split("T")[0]
  );

  const navigate = useNavigate();
  const tableRef = useRef(null);

  // Получение данных с сервера
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/drivers");
        setDrivers(response.data);
      } catch (error) {
        console.error("Error fetching drivers:", error);
      }
    };

    fetchData();
    fetchDrivers();
  }, []);

  // Функция выхода из системы
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username"); // Очистка имени пользователя при выходе
    navigate("/login");
  };

  // Сортировка данных по водителям, затем по дате и перемещение строк с классом 'highlight' в конец
  const sortData = (data) => {
    const highlightedItems = data.filter(
      (item) => item.colorClass === "highlight"
    );
    const nonHighlightedItems = data.filter(
      (item) => item.colorClass !== "highlight"
    );

    // Сортируем только невыделенные элементы по водителям
    const sortedNonHighlightedItems = nonHighlightedItems.sort((a, b) => {
      if (a.driver < b.driver) return -1;
      if (a.driver > b.driver) return 1;
      return 0;
    });

    // Объединяем отсортированные элементы и выделенные элементы
    return [...sortedNonHighlightedItems, ...highlightedItems];
  };

  const sortedData = sortData(data);

  // Функция для добавления строк на каждый новый день
  const addEntriesForSelectedDate = async () => {
    try {
      const newEntries = hardcodedData.map((item) => ({
        ...item,
        date: selectedDate,
        updatedBy: localStorage.getItem("username"), // Добавляем имя пользователя
      }));

      // Добавление новых записей
      await Promise.all(
        newEntries.map((entry) =>
          axios.post("http://localhost:5000/api/data", entry)
        )
      );

      // Перезагрузка данных с сервера для синхронизации
      const response = await axios.get("http://localhost:5000/api/data");
      setData(response.data);
    } catch (error) {
      console.error("Error adding entries:", error);
    }
  };

  // Добавление одной строки данных
  const addNewItem = async () => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/data",
        { ...newItem, updatedBy: localStorage.getItem("username") } // Добавляем имя пользователя
      );
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  // Добавление строки с определенным классом
  const addNewItemWithClass = async (className) => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", {
        ...newItem,
        colorClass: className, // Добавляем класс
        updatedBy: localStorage.getItem("username"), // Добавляем имя пользователя
      });
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data with class:", error);
    }
  };

  const handleInputChange = (e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;
    const username = localStorage.getItem("username");

    setData(
      data.map((item) =>
        item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
      )
    );

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...data.find((item) => item._id === itemId),
        [fieldName]: updatedValue,
        updatedBy: username, // Добавляем имя пользователя
      })
      .catch((error) => console.error("Error saving data:", error));
  };

  const handleDateChange = (e, itemId) => {
    const { value } = e.target;
    const username = localStorage.getItem("username");

    setData(
      data.map((item) =>
        item._id === itemId ? { ...item, date: value } : item
      )
    );

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...data.find((item) => item._id === itemId),
        date: value,
        updatedBy: username, // Добавляем имя пользователя
      })
      .catch((error) => console.error("Error updating date:", error));
  };

  const handleAddNewDriver = async () => {
    if (newDriver.trim() === "") return;

    try {
      const response = await axios.post("http://localhost:5000/api/drivers", {
        name: newDriver,
      });
      setDrivers([...drivers, response.data]);
      setNewDriver("");
    } catch (error) {
      console.error("Error adding driver:", error);
    }
  };

  // Функция для группировки данных по месяцам и дням
  const groupByMonthAndDay = (data) => {
    return data.reduce((groups, item) => {
      const date = new Date(item.date);
      const monthYear = date.toLocaleString("default", { month: "long" });
      const day = date.getDate();

      if (!groups[monthYear]) {
        groups[monthYear] = {};
      }

      if (!groups[monthYear][day]) {
        groups[monthYear][day] = [];
      }

      groups[monthYear][day].push(item);
      return groups;
    }, {});
  };

  // Прокрутка к текущему дню
  useEffect(() => {
    if (tableRef.current) {
      const currentDate = new Date().toISOString().split("T")[0];
      const dayRow = tableRef.current.querySelector(
        `tr[data-date="${currentDate}"]`
      );
      if (dayRow) {
        dayRow.scrollIntoView({ behavior: "smooth", block: "center" });
      }
    }
  }, [data]);

  return (
    <div>
      <h1>Data Table</h1>
      <button onClick={addEntriesForSelectedDate}>
        Add 37 Entries for Selected Date
      </button>
      <button onClick={addNewItem}>Add New Item</button>
      <button onClick={() => addNewItemWithClass("highlight")}>
        Add New Item with Highlight
      </button>
      <button onClick={handleLogout}>Logout</button> {/* Кнопка для выхода */}
      <div>
        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
        />
        <input
          type="text"
          value={newDriver}
          onChange={(e) => setNewDriver(e.target.value)}
          placeholder="Enter new driver name"
        />
        <button onClick={handleAddNewDriver}>Add New Driver</button>
      </div>
      {Object.entries(groupByMonthAndDay(sortedData)).map(([month, days]) => (
        <div key={month}>
          <h2>{month}</h2>
          {Object.entries(days).map(([day, items]) => (
            <div key={day}>
              <h3>Day {day}</h3>
              <table ref={tableRef}>
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Brand</th>
                    <th>Driver</th>
                    <th>Customer</th>
                    <th>Route Number</th>
                    <th>Hours</th>
                    <th>True/False</th>
                    <th>Vehicle Number</th>
                    <th>Price</th>
                  </tr>
                </thead>
                <tbody>
                  {items.map((item) => (
                    <tr
                      key={item._id}
                      className={item.colorClass || ""}
                      data-date={item.date}
                    >
                      <td>
                        <input
                          type="date"
                          value={
                            new Date(item.date).toISOString().split("T")[0]
                          }
                          onChange={(e) => handleDateChange(e, item._id)}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.brand || ""}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "brand")
                          }
                        />
                      </td>
                      <td>
                        <select
                          value={item.driver || ""}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "driver")
                          }
                        >
                          <option value="">Select a driver</option>
                          {drivers.map((driver) => (
                            <option key={driver._id} value={driver._id}>
                              {driver.name}
                            </option>
                          ))}
                        </select>
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.customer || ""}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "customer")
                          }
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.routeNumber || ""}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "routeNumber")
                          }
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.hours || ""}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "hours")
                          }
                        />
                      </td>
                      <td>
                        <input
                          type="checkbox"
                          checked={item.isTrue || false}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "isTrue")
                          }
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.vehicleNumber || ""}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "vehicleNumber")
                          }
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.price || ""}
                          onChange={(e) =>
                            handleInputChange(e, item._id, "price")
                          }
                        />
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Home;
