import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);

  // Группировка данных по месяцам и дням
  const groupByMonthAndDay = (data) => {
    return data.reduce((groups, item) => {
      const date = new Date(item.date);
      const monthYear = date.toLocaleString("default", { month: "long", year: "numeric" });
      const day = date.getDate();

      if (!groups[monthYear]) {
        groups[monthYear] = {};
      }

      if (!groups[monthYear][day]) {
        groups[monthYear][day] = [];
      }

      groups[monthYear][day].push(item);
      return groups;
    }, {});
  };

  // Сортировка данных внутри каждого дня
  const sortDataWithinDay = (data) => {
    return data.sort((a, b) => {
      // Сначала сортируем по водителям
      if (a.driver < b.driver) return -1;
      if (a.driver > b.driver) return 1;

      // Затем сортируем по классу "highlight"
      if (a.colorClass === "highlight" && b.colorClass !== "highlight") {
        return 1; // Строки с классом "highlight" должны быть ниже
      }
      if (a.colorClass !== "highlight" && b.colorClass === "highlight") {
        return -1; // Строки без класса "highlight" должны быть выше
      }

      return 0; // Если и водитель, и класс одинаковы
    });
  };

  // Получение данных с сервера и их группировка
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        const groupedData = groupByMonthAndDay(response.data);

        // Сортировка данных внутри каждого дня
        Object.keys(groupedData).forEach((monthYear) => {
          Object.keys(groupedData[monthYear]).forEach((day) => {
            groupedData[monthYear][day] = sortDataWithinDay(groupedData[monthYear][day]);
          });
        });

        setData(groupedData);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  // Обработка изменения данных
  const handleInputChange = (e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;

    // Обновляем данные в локальном состоянии
    setData((prevData) => {
      const newData = { ...prevData };
      Object.keys(newData).forEach((monthYear) => {
        Object.keys(newData[monthYear]).forEach((day) => {
          newData[monthYear][day] = newData[monthYear][day].map((item) =>
            item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
          );
        });
      });
      return newData;
    });

    // Обновляем данные на сервере
    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        [fieldName]: updatedValue,
      })
      .catch((error) => console.error("Error updating data:", error));
  };

  const handleDateChange = (e, itemId) => {
    const { value } = e.target;

    // Обновляем дату
    setData((prevData) => {
      const newData = { ...prevData };
      Object.keys(newData).forEach((monthYear) => {
        Object.keys(newData[monthYear]).forEach((day) => {
          newData[monthYear][day] = newData[monthYear][day].map((item) =>
            item._id === itemId ? { ...item, date: value } : item
          );
        });
      });
      return newData;
    });

    // Обновляем данные на сервере
    axios
      .put(`http://localhost:5000/api/data/${itemId}`, { date: value })
      .catch((error) => console.error("Error updating date:", error));
  };

  return (
    <div>
      <h1>Data Table</h1>
      {Object.entries(data).map(([month, days]) => (
        <div key={month}>
          <h2>{month}</h2>
          {Object.entries(days).map(([day, items]) => (
            <div key={day}>
              <h3>Day {day}</h3>
              <table>
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Brand</th>
                    <th>Driver</th>
                    <th>Customer</th>
                    <th>Route Number</th>
                    <th>Hours</th>
                    <th>True/False</th>
                    <th>Vehicle Number</th>
                    <th>Price</th>
                  </tr>
                </thead>
                <tbody>
                  {items.map((item) => (
                    <tr key={item._id} className={item.colorClass || ""}>
                      <td>
                        <input
                          type="date"
                          value={new Date(item.date).toISOString().split("T")[0]}
                          onChange={(e) => handleDateChange(e, item._id)}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.brand || ""}
                          onChange={(e) => handleInputChange(e, item._id, "brand")}
                        />
                      </td>
                      <td>
                        <select
                          value={item.driver || ""}
                          onChange={(e) => handleInputChange(e, item._id, "driver")}
                        >
                          <option value="">Select a driver</option>
                          {drivers.map((driver) => (
                            <option key={driver._id} value={driver._id}>
                              {driver.name}
                            </option>
                          ))}
                        </select>
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.customer || ""}
                          onChange={(e) => handleInputChange(e, item._id, "customer")}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.routeNumber || ""}
                          onChange={(e) => handleInputChange(e, item._id, "routeNumber")}
                        />
                      </td>
                      <td>
                        <input
                          type="number"
                          value={item.hours || ""}
                          onChange={(e) => handleInputChange(e, item._id, "hours")}
                        />
                      </td>
                      <td>
                        <input
                          type="checkbox"
                          checked={item.isTrue || false}
                          onChange={(e) => handleInputChange(e, item._id, "isTrue")}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.vehicleNumber || ""}
                          onChange={(e) => handleInputChange(e, item._id, "vehicleNumber")}
                        />
                      </td>
                      <td>
                        <input
                          type="number"
                          value={item.price || ""}
                          onChange={(e) => handleInputChange(e, item._id, "price")}
                        />
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Home;
