import React, { useState, useEffect } from "react";
import axios from "axios";
import { hardcodedData } from "./hardcodedData";
import "./styles.css";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [newDriver, setNewDriver] = useState("");
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split("T")[0],
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
    colorClass: "",
  });
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().split("T")[0]
  );
  const [currentUser, setCurrentUser] = useState("current_user"); // Замените на текущего пользователя

  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/drivers");
        setDrivers(response.data);
      } catch (error) {
        console.error("Error fetching drivers:", error);
      }
    };

    fetchData();
    fetchDrivers();
  }, []);

  const sortedData = data.sort((a, b) => new Date(a.date) - new Date(b.date));

  const addEntriesForSelectedDate = async () => {
    try {
      const newEntries = hardcodedData.map((item) => ({
        ...item,
        date: selectedDate,
        changedBy: currentUser, // Добавляем пользователя
      }));

      await Promise.all(
        newEntries.map((entry) =>
          axios.post("http://localhost:5000/api/data", entry)
        )
      );

      const response = await axios.get("http://localhost:5000/api/data");
      setData(response.data);
    } catch (error) {
      console.error("Error adding entries:", error);
    }
  };

  const addNewItem = async () => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/data",
        { ...newItem, changedBy: currentUser } // Добавляем пользователя
      );
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setNewItem((prev) => ({ ...prev, [name]: value }));
  };

  const handleDriverChange = (event) => {
    setNewDriver(event.target.value);
  };

  const addDriver = async () => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/drivers",
        { name: newDriver, changedBy: currentUser } // Добавляем пользователя
      );
      setDrivers([...drivers, response.data]);
      setNewDriver("");
    } catch (error) {
      console.error("Error adding driver:", error);
    }
  };

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  return (
    <div className="container">
      <h1>Data Management</h1>
      <input type="date" value={selectedDate} onChange={handleDateChange} />
      <button onClick={addEntriesForSelectedDate}>
        Add Entries for Selected Date
      </button>

      <h2>Add New Item</h2>
      <form>
        <input
          type="date"
          name="date"
          value={newItem.date}
          onChange={handleChange}
        />
        <input
          type="text"
          name="brand"
          value={newItem.brand}
          onChange={handleChange}
          placeholder="Brand"
        />
        <input
          type="text"
          name="driver"
          value={newItem.driver}
          onChange={handleChange}
          placeholder="Driver"
        />
        <input
          type="text"
          name="customer"
          value={newItem.customer}
          onChange={handleChange}
          placeholder="Customer"
        />
        <input
          type="text"
          name="routeNumber"
          value={newItem.routeNumber}
          onChange={handleChange}
          placeholder="Route Number"
        />
        <input
          type="number"
          name="hours"
          value={newItem.hours}
          onChange={handleChange}
          placeholder="Hours"
        />
        <input
          type="checkbox"
          name="isTrue"
          checked={newItem.isTrue}
          onChange={(e) => setNewItem({ ...newItem, isTrue: e.target.checked })}
        />
        <input
          type="text"
          name="vehicleNumber"
          value={newItem.vehicleNumber}
          onChange={handleChange}
          placeholder="Vehicle Number"
        />
        <input
          type="number"
          name="price"
          value={newItem.price}
          onChange={handleChange}
          placeholder="Price"
        />
        <input
          type="text"
          name="colorClass"
          value={newItem.colorClass}
          onChange={handleChange}
          placeholder="Color Class"
        />
        <button type="button" onClick={addNewItem}>
          Add New Item
        </button>
      </form>

      <h2>Add New Driver</h2>
      <input
        type="text"
        value={newDriver}
        onChange={handleDriverChange}
        placeholder="Driver Name"
      />
      <button onClick={addDriver}>Add Driver</button>

      <h2>Data</h2>
      <ul>
        {sortedData.map((item) => (
          <li key={item._id}>{JSON.stringify(item)}</li>
        ))}
      </ul>

      <h2>Drivers</h2>
      <ul>
        {drivers.map((driver) => (
          <li key={driver._id}>{driver.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
