import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import ReCAPTCHA from "react-google-recaptcha"; // Импортируем ReCAPTCHA

const Login = ({ setAuth }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [captchaToken, setCaptchaToken] = useState(null);
  const navigate = useNavigate();
  const apiUrl = "https://bam-app-489c6c1370a9.herokuapp.com";

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${apiUrl}/api/login`, {
        username,
        password,
        captchaToken, // отправка капчи
      });
      document.cookie = `token=${response.data.token}; Secure; HttpOnly; SameSite=Strict`;
      setAuth({
        isAuthenticated: true,
        user: { username },
      });
      setErrorMessage("");
      navigate("/");
    } catch (error) {
      setErrorMessage("Invalid username or password. Please try again.");
    }
  };

  const handleCaptcha = (token) => {
    setCaptchaToken(token);
  };

  return (
    <div className="login">
      <h1>APCR</h1>
      <h2>Войдите в систему</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
        />
        <ReCAPTCHA
          sitekey="6LeRgUwqAAAAAGRLGfEaXPY_JccXocPIuQBvDeg2"
          onChange={handleCaptcha}
        />
        <button type="submit" disabled={!captchaToken}>
          Войти
        </button>
      </form>
      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
    </div>
  );
};

export default Login;
