// Home.js
const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    const response = await axios.post("http://localhost:5000/api/data", form);
    setData([...data, response.data]); // Обновляем список данных с новым элементом
    setForm({
      date: "",
      brand: "",
      driver: "",
      customer: "",
      routeNumber: "",
      hours: "",
      status: false,
      licensePlate: "",
      price: "",
    }); // Сбрасываем форму
  } catch (error) {
    console.error("Error adding data:", error);
  }
};
