      try {
        const response = await axios.put(`${apiUrl}/api/data/${itemId}`, {
          [fieldName]: updatedValue,
          updatedBy: username,
        });
        return response.data; // Возвращаем обновленные данные
      } catch (error) {
        console.error("Error saving data:", error);
        throw error; // Пробрасываем ошибку
      }
    },
    [apiUrl]
  );

  useEffect(() => {
