        try {
          const responseData = await updateData(
            itemId,
            fieldName,
            updatedValue
          );
          setData((prevData) =>
            prevData.map((item) => (item._id === itemId ? responseData : item))
          );
        } catch (error) {
          console.error("Error updating data:", error);
        }
