import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import dayjs from "dayjs";

const Profile = () => {
  const [items, setItems] = useState([]);
  const [user, setUser] = useState(null);
  const [drivers, setDrivers] = useState([]); // Список водителей из базы данных
  const [selectedDriver, setSelectedDriver] = useState(""); // Выбранный водитель
  const apiUrl = "https://bam-app-489c6c1370a9.herokuapp.com";

  // Получение данных пользователя
  useEffect(() => {
    const fetchData = async () => {
      try {
        const username = localStorage.getItem("username");
        setUser(username);

        const response = await axios.get(`${apiUrl}/api/data`);
        const userItems = response.data.filter(
          (item) => item.user === username
        );
        setItems(userItems);
      } catch (error) {
        console.error("Ошибка при загрузке данных:", error);
      }
    };

    fetchData();
  }, []);

  // Получение списка водителей
  useEffect(() => {
    const fetchDrivers = async () => {
      try {
        const driverResponse = await axios.get(`${apiUrl}/api/drivers`);
        setDrivers(driverResponse.data); // Предполагается, что API возвращает массив водителей
      } catch (error) {
        console.error("Ошибка при загрузке списка водителей:", error);
      }
    };

    fetchDrivers();
  }, []);

  if (!user) {
    return <p>Загрузка...</p>;
  }

  // Фильтруем заявки по выбранному водителю и наличию colorClass
  const filteredItems = items.filter((item) => {
    return (
      (!selectedDriver || item.driver === selectedDriver) && // Если водитель выбран, фильтруем по нему
      item.colorClass // Проверяем наличие colorClass
    );
  });

  // Заявки для выбранного водителя
  const driverItems = items.filter((item) => item.driver === selectedDriver);

  return (
    <div>
      <Link to="/">
        <button>Go to Home</button>
      </Link>
      <h1>Профиль пользователя: {user}</h1>

      {/* Инпут для выбора водителя */}
      <div>
        <label htmlFor="driver">Выберите водителя:</label>
        <select
          id="driver"
          value={selectedDriver}
          onChange={(e) => setSelectedDriver(e.target.value)}
        >
          <option value="">Все водители</option>
          {drivers.map((driver) => (
            <option key={driver._id} value={driver.name}>
              {driver.name}
            </option>
          ))}
        </select>
      </div>

      <p>Количество созданных элементов: {filteredItems.length}</p>

      {/* Отображаем заявки выбранного водителя */}
      <div>
        <h2>Заявки водителя: {selectedDriver || "Все"}</h2>
        {driverItems.length > 0 ? (
          <ul>
            {driverItems.map((item) => (
              <li key={item._id}>
                {item.brand} - {item.price || "Цена не указана"} -{" "}
                {item.calcPay || "Без calcPay"} - {item.date}
              </li>
            ))}
          </ul>
        ) : (
          <p>Нет заявок для отображения.</p>
        )}
      </div>
    </div>
  );
};

export default Profile;
