    const sortedGroups = driverGroupedData.map((group) =>
      group.sort((a, b) => a.orderIndex - b.orderIndex)
    );

    // Объединяем данные текущей даты
    return sortedGroups.flat(); // Возвращаем объединенные данные для данной даты
  });

