                        <option
                          value="Оплачен"
                          style={{ color: "green", fontWeight: "bold" }}
                        >
                          Оплачен
                        </option>
                        <option
                          value="Частично"
                          style={{ color: "gold", fontWeight: "bold" }}
                        >
                          Частично
                        </option>
                        <option
                          value="Нет"
                          style={{ color: "red", fontWeight: "bold" }}
                        >
                          Нет
                        </option>
                        <option
                          value="Отсрочка"
                          style={{ color: "yellow", fontWeight: "bold" }}
                        >
                          Отсрочка
                        </option>
                      </select>
                    </td>
                    <td className="disabled">
                      <input
                        type="text"
                        value={item.calcPay || ""}
                        onChange={(e) =>
                          handleInputChange(e, item._id, "calcPay")
                        }
                      />
                    </td>
                    <td className="disabled">
                      <input
                        type="text"
                        value={item.comment || ""}
                        onChange={(e) =>
                          handleInputChange(e, item._id, "comment")
                        }
                      />
                    </td>
                    <td>
                      <select
                        value={item.user || ""}
                        onChange={(e) => handleInputChange(e, item._id, "user")}
                      >
                        <option value="">Выбрать</option>
                        {users.map((user) => (
                          <option key={user._id} value={user.username}>
                            {user.name}
                          </option>
                        ))}
                      </select>
                    </td>
                    <td>
                      <span>{item.updatedBy || "-"}</span>
                    </td>
                    <td>
                      <button
                        className="btn-del"
                        onMouseEnter={() => handleMouseEnter(item._id)}
                        onMouseLeave={handleMouseLeave}
                        onClick={() => handleDeleteWithConfirmation(item._id)}
                        onTouchStart={() => handleMouseEnter(item._id)}
                        onTouchEnd={handleMouseLeave}
                      >
                        Удалить
                      </button>

                      {/* <button
                        onClick={() =>
                          moveItemUp(item._id, item.orderIndex, day)
                        }
                        disabled={index === 0} // Отключить кнопку, если это первый элемент
                      >
                        ↑ Вверх
                      </button>
                      <button
                        onClick={() =>
                          moveItemDown(item._id, item.orderIndex, day)
                        }
                        disabled={index === groupedByDayData[day].length - 1} // Отключить кнопку, если это последний элемент
                      >
                        ↓ Вниз
                      </button> */}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
