import React, { useState, useEffect } from "react";
import Confetti from "react-confetti";
import "./newYearPopup.css"; // Путь к вашему стилю

const NewYearPopup = () => {
  const [showPopup, setShowPopup] = useState(true); // Стейт для отображения попапа
  const [confetti, setConfetti] = useState(false); // Стейт для конфетти
  const [windowWidth, setWindowWidth] = useState(window.innerWidth); // Ширина окна
  const [windowHeight, setWindowHeight] = useState(window.innerHeight); // Высота окна

  // Обработчик события изменения размера окна
  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
      setWindowHeight(window.innerHeight);
    };

    window.addEventListener("resize", handleResize); // Слушаем событие resize
    return () => {
      window.removeEventListener("resize", handleResize); // Очистка события при размонтировании компонента
    };
  }, []);

  useEffect(() => {
    let confettiTimer;
    if (confetti) {
      // Конфетти будет показываться 3 минуты (180000 миллисекунд)
      confettiTimer = setTimeout(() => {
        setConfetti(false); // Останавливаем конфетти через 3 минуты
      }, 180000); // 180000 миллисекунд = 3 минуты
    }
    return () => clearTimeout(confettiTimer); // Очистка таймера
  }, [confetti]);

  const handleClick = () => {
    setConfetti(true); // Включаем конфетти
    setShowPopup(false); // Скрываем попап сразу после нажатия
  };

  return (
    <>
      {showPopup && (
        <div className="popup-overlay">
          {confetti && (
            <Confetti
              width={windowWidth} // Используем ширину окна
              height={windowHeight} // Используем высоту окна
              numberOfPieces={500} // Увеличиваем количество конфетти
              gravity={0.2} // Уменьшаем гравитацию для плавного падения
              recycle={false} // Конфетти не будет повторяться после того, как они исчезнут
            />
          )}
          <div className="popup-content">
            <img
              className="popup-img"
              src="https://example.com/your-image.jpg" // Замените на реальный URL картинки
              alt="С Новым годом!"
            />
            <h2>С Новым годом!</h2>
            <p>Пусть этот год принесет счастье и радость!</p>

            {/* Пример иконки с Font Awesome */}
            <div className="icon-container">
              <i className="fas fa-snowflake"></i>{" "}
              {/* Замените на другую иконку, если нужно */}
            </div>

            <button className="popup-button" onClick={handleClick}>
              Нажми на меня
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default NewYearPopup;
