import React, { useState, useEffect } from "react";
import Confetti from "react-confetti";
import "../../src/"; // Подключаем стили для попапа

const NewYearPopup = () => {
  const [showPopup, setShowPopup] = useState(false);
  const [confetti, setConfetti] = useState(false);

  // Проверяем localStorage для показа попапа только при первой загрузке
  useEffect(() => {
    const isFirstVisit = localStorage.getItem("firstVisit");

    if (!isFirstVisit) {
      setShowPopup(true);
      localStorage.setItem("firstVisit", "true");
    }
  }, []);

  const handleClick = () => {
    setConfetti(true); // Показываем конфетти
    setTimeout(() => setConfetti(false), 5000); // Конфетти исчезают через 5 секунд
    setShowPopup(false); // Скрываем попап
  };

  return (
    <>
      {showPopup && (
        <div className="popup-overlay">
          {confetti && <Confetti />}
          <div className="popup-content">
            <img
              className="popup-img"
              src="https://example.com/your-image.jpg" // Замените на свой URL изображения
              alt="С Новым годом!"
            />
            <h2>С Новым годом!</h2>
            <p>Пусть этот год принесет счастье и радость!</p>
            <button className="popup-button" onClick={handleClick}>
              Нажми на меня
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default NewYearPopup;
