import React, { useState, useEffect } from "react";
import axios from "axios";

const Profile = () => {
  const [items, setItems] = useState([]);
  const [user, setUser] = useState(null); // Информация о пользователе

  // Получение данных пользователя и созданных им элементов
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const username = localStorage.getItem("username");
        const userResponse = await axios.get(`/api/user/${username}`);
        setUser(userResponse.data);

        const itemsResponse = await axios.get(
          `/api/items?createdBy=${username}`
        );
        setItems(itemsResponse.data);
      } catch (error) {
        console.error("Ошибка при загрузке данных:", error);
      }
    };

    fetchUserData();
  }, []);

  if (!user) {
    return <p>Загрузка...</p>;
  }

  return (
    <div>
      <h1>Профиль пользователя: {user.username}</h1>
      <p>Количество созданных элементов: {items.length}</p>

      <h2>Все созданные элементы:</h2>
      <ul>
        {items.map((item) => (
          <li key={item._id}>
            {item.name} — {item.description}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Profile;
