import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { hardcodedData } from "./hardcodedData";
import "./styles.css";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [newDriver, setNewDriver] = useState("");
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split("T")[0],
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
    colorClass: "",
  });
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().split("T")[0]
  );

  const navigate = useNavigate();
  
  // Реф для прокрутки к текущему дню
  const currentDayRef = useRef(null);

  // Получение данных с сервера
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/drivers");
        setDrivers(response.data);
      } catch (error) {
        console.error("Error fetching drivers:", error);
      }
    };

    fetchData();
    fetchDrivers();
  }, []);

  // Прокрутка к текущему дню после загрузки данных
  useEffect(() => {
    if (currentDayRef.current) {
      currentDayRef.current.scrollIntoView({ behavior: "smooth", block: "start" });
    }
  }, [data]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  // Добавление строк на каждый новый день
  const addEntriesForSelectedDate = async () => {
    try {
      const newEntries = hardcodedData.map((item) => ({
        ...item,
        date: selectedDate,
      }));

      await Promise.all(
        newEntries.map((entry) =>
          axios.post("http://localhost:5000/api/data", entry)
        )
      );

      const response = await axios.get("http://localhost:5000/api/data");
      setData(response.data);
    } catch (error) {
      console.error("Error adding entries:", error);
    }
  };

  // Добавление новой строки
  const addNewItem = async () => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/data",
        newItem
      );
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  // Добавление строки с классом
  const addNewItemWithClass = async (className) => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", {
        ...newItem,
        colorClass: className,
      });
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data with class:", error);
    }
  };

  const handleInputChange = (e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;

    setData(
      data.map((item) =>
        item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
      )
    );

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...data.find((item) => item._id === itemId),
        [fieldName]: updatedValue,
      })
      .catch((error) => console.error("Error saving data:", error));
  };

  const handleDateChange = (e, itemId) => {
    const { value } = e.target;

    setData(
      data.map((item) =>
        item._id === itemId ? { ...item, date: value } : item
      )
    );

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...data.find((item) => item._id === itemId),
        date: value,
      })
      .catch((error) => console.error("Error updating date:", error));
  };

  const handleAddNewDriver = async () => {
    if (newDriver.trim() === "") return;

    try {
      const response = await axios.post("http://localhost:5000/api/drivers", {
        name: newDriver,
      });
      setDrivers([...drivers, response.data]);
      setNewDriver("");
    } catch (error) {
      console.error("Error adding driver:", error);
    }
  };

  // Сортировка данных по водителям и цвету (highlight)
  const sortData = (data) => {
    return data
      .sort((a, b) => {
        if (a.colorClass === "highlight" && b.colorClass !== "highlight") {
          return 1; // Строки с классом "highlight" должны быть ниже
        }
        if (a.colorClass !== "highlight" && b.colorClass === "highlight") {
          return -1; // Строки без класса "highlight" должны быть выше
        }
        return 0; // Если оба или ни один не имеют класс "highlight", оставляем на месте
      })
      .sort((a, b) => {
        if (a.driver < b.driver) return -1;
        if (a.driver > b.driver) return 1;
        return 0; // Водители с одинаковым именем должны быть рядом
      });
  };

  const groupedData = sortData(data);

  // Определяем текущий день для прокрутки
  const isToday = (itemDate) => {
    const today = new Date();
    const itemDay = new Date(itemDate);
    return (
      today.getDate() === itemDay.getDate() &&
      today.getMonth() === itemDay.getMonth() &&
      today.getFullYear() === itemDay.getFullYear()
    );
  };

  return (
    <div>
      <h1>Data Table</h1>
      <button onClick={addEntriesForSelectedDate}>
        Add 37 Entries for Selected Date
      </button>
      <button onClick={addNewItem}>Add New Item</button>
      <button onClick={() => addNewItemWithClass("highlight")}>
        Add New Item with Highlight
      </button>
      <button onClick={handleLogout}>Logout</button>
      <div>
        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
        />
        <input
          type="text"
          value={newDriver}
          onChange={(e) => setNewDriver(e.target.value)}
          placeholder="Enter new driver name"
        />
        <button onClick={handleAddNewDriver}>Add New Driver</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Brand</th>
            <th>Driver</th>
            <th>Customer</th>
            <th>Route Number</th>
            <th>Hours</th>
            <th>True/False</th>
            <th>Vehicle Number</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {groupedData.map((item) => (
            <tr
              key={item._id}
              ref={isToday(item.date) ? currentDayRef : null}
              className={item.colorClass || ""}
            >
              <td>
                <input
                  type="date"
                  value={new Date(item.date).toISOString().split("T")[0]}
                  onChange={(e) => handleDateChange(e, item._id)}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.brand || ""}
                  onChange={(e) => handleInputChange(e, item._id, "brand")}
                />
              </td>
              <td>
                <select
                  value={item.driver || ""}
                  onChange={(e) => handleInputChange(e, item._id, "driver")}
                >
                  <option value="">Select Driver</option>
                  {drivers.map((driver) => (
                    <option key={driver._id} value={driver._id}>
                      {driver.name}
                    </option>
                  ))}
                </select>
              </td>
              <td>
                <input
                  type="text"
                  value={item.customer || ""}
                  onChange={(e) => handleInputChange(e, item._id, "customer")}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.routeNumber || ""}
                  onChange={(e) =>
                    handleInputChange(e, item._id, "routeNumber")
                  }
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.hours || ""}
                  onChange={(e) => handleInputChange(e, item._id, "hours")}
                />
              </td>
              <td>
                <input
                  type="checkbox"
                  checked={item.isTrue || false}
                  onChange={(e) => handleInputChange(e, item._id, "isTrue")}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.vehicleNumber || ""}
                  onChange={(e) =>
                    handleInputChange(e, item._id, "vehicleNumber")
                  }
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.price || ""}
                  onChange={(e) => handleInputChange(e, item._id, "price")}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
