// home.js

import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split("T")[0],
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/drivers");
        setDrivers(response.data);
      } catch (error) {
        console.error("Error fetching drivers:", error);
      }
    };

    fetchData();
    fetchDrivers();
  }, []);

  const handleInputChange = (e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;

    setData(
      data.map((item) =>
        item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
      )
    );

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...data.find((item) => item._id === itemId),
        [fieldName]: updatedValue,
      })
      .catch((error) => console.error("Error saving data:", error));
  };

  const handleNewItemChange = (e) => {
    const { value, name } = e.target;
    setNewItem({
      ...newItem,
      [name]: value,
    });
  };

  const addNewItem = async () => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/data",
        newItem
      );
      setData([...data, response.data]);
      setNewItem({
        date: new Date().toISOString().split("T")[0],
        brand: "",
        driver: "",
        customer: "",
        routeNumber: "",
        hours: "",
        isTrue: false,
        vehicleNumber: "",
        price: "",
      });
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  return (
    <div>
      <h1>Data Table</h1>
      <button onClick={addNewItem}>Add New Item</button>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Brand</th>
            <th>Driver</th>
            <th>Customer</th>
            <th>Route Number</th>
            <th>Hours</th>
            <th>True/False</th>
            <th>Vehicle Number</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td>{new Date(item.date).toLocaleDateString()}</td>
              <td>
                <input
                  type="text"
                  value={item.brand || ""}
                  onChange={(e) => handleInputChange(e, item._id, "brand")}
                />
              </td>
              <td>
                <select
                  value={item.driver || ""}
                  onChange={(e) => handleInputChange(e, item._id, "driver")}
                >
                  <option value="">Select driver</option>
                  {drivers.map((driver) => (
                    <option key={driver._id} value={driver.name}>
                      {driver.name}
                    </option>
                  ))}
                </select>
              </td>
              <td>
                <input
                  type="text"
                  value={item.customer || ""}
                  onChange={(e) => handleInputChange(e, item._id, "customer")}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.routeNumber || ""}
                  onChange={(e) =>
                    handleInputChange(e, item._id, "routeNumber")
                  }
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.hours || ""}
                  onChange={(e) => handleInputChange(e, item._id, "hours")}
                />
              </td>
              <td>
                <input
                  type="checkbox"
                  checked={item.isTrue || false}
                  onChange={(e) => handleInputChange(e, item._id, "isTrue")}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.vehicleNumber || ""}
                  onChange={(e) =>
                    handleInputChange(e, item._id, "vehicleNumber")
                  }
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.price || ""}
                  onChange={(e) => handleInputChange(e, item._id, "price")}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <h2>Add New Item</h2>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          addNewItem();
        }}
      >
        <label>
          Date:
          <input
            type="date"
            name="date"
            value={newItem.date}
            onChange={handleNewItemChange}
            readOnly
          />
        </label>
        <label>
          Brand:
          <input
            type="text"
            name="brand"
            value={newItem.brand}
            onChange={handleNewItemChange}
          />
        </label>
        <label>
          Driver:
          <select
            name="driver"
            value={newItem.driver}
            onChange={handleNewItemChange}
          >
            <option value="">Select driver</option>
            {drivers.map((driver) => (
              <option key={driver._id} value={driver.name}>
                {driver.name}
              </option>
            ))}
          </select>
        </label>
        <label>
          Customer:
          <input
            type="text"
            name="customer"
            value={newItem.customer}
            onChange={handleNewItemChange}
          />
        </label>
        <label>
          Route Number:
          <input
            type="text"
            name="routeNumber"
            value={newItem.routeNumber}
            onChange={handleNewItemChange}
          />
        </label>
        <label>
          Hours:
          <input
            type="text"
            name="hours"
            value={newItem.hours}
            onChange={handleNewItemChange}
          />
        </label>
        <label>
          TRUE/FALSE:
          <input
            type="checkbox"
            name="isTrue"
            checked={newItem.isTrue}
            onChange={handleNewItemChange}
          />
        </label>
        <label>
          Vehicle Number:
          <input
            type="text"
            name="vehicleNumber"
            value={newItem.vehicleNumber}
            onChange={handleNewItemChange}
          />
        </label>
        <label>
          Price:
          <input
            type="text"
            name="price"
            value={newItem.price}
            onChange={handleNewItemChange}
          />
        </label>
        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default Home;
