import React, { useState, useEffect, useRef, useCallback } from "react";
import axios from "axios";
import { hardcodedData } from "./hardcodedData";
import "./styles.css";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [newDriver, setNewDriver] = useState("");
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split("T")[0],
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
    colorClass: "",
  });
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().split("T")[0]
  );
  const navigate = useNavigate();
  const currentDayRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Ошибка при получении данных:", error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/drivers");
        setDrivers(response.data);
      } catch (error) {
        console.error("Ошибка при получении водителей:", error);
      }
    };

    fetchData();
    fetchDrivers();
  }, []);

  useEffect(() => {
    if (currentDayRef.current) {
      currentDayRef.current.scrollIntoView({ behavior: "smooth", block: "start" });
    }
  }, [data]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const replaceSoftSign = (str) => {
    return str.replace(/ъ/g, '');
  };

  const sortedData = data
    .sort((a, b) => {
      if (a.colorClass === 'highlight' && b.colorClass !== 'highlight') return 1;
      if (a.colorClass !== 'highlight' && b.colorClass === 'highlight') return -1;

      const driverComparison = a.driver.localeCompare(b.driver);
      if (driverComparison !== 0) return driverComparison;

      return new Date(a.date) - new Date(b.date);
    });

  const addEntriesForSelectedDate = async () => {
    try {
      const newEntries = hardcodedData.map((item) => ({
        ...item,
        date: selectedDate,
      }));

      await Promise.all(
        newEntries.map((entry) =>
          axios.post("http://localhost:5000/api/data", entry)
        )
      );

      const response = await axios.get("http://localhost:5000/api/data");
      setData(response.data);
    } catch (error) {
      console.error("Ошибка при добавлении записей:", error);
    }
  };

  const addNewItem = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", newItem);
      setData([...data, response.data]);
    } catch (error) {
      console.error("Ошибка при добавлении данных:", error);
    }
  };

  const addNewItemWithClass = async (className) => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", {
        ...newItem,
        colorClass: className,
      });
      setData([...data, response.data]);
    } catch (error) {
      console.error("Ошибка при добавлении данных с классом:", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/data/${id}`);
      setData(data.filter(item => item._id !== id));
    } catch (error) {
      console.error("Ошибка при удалении данных:", error);
    }
  };

  const handleDeleteWithConfirmation = (id) => {
    const isConfirmed = window.confirm("Вы уверены, что хотите удалить этот элемент?");
    if (isConfirmed) handleDelete(id);
  };

  const handleInputChange = useCallback((e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;

    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
    );
    setData(updatedData);

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        [fieldName]: updatedValue,
      })
      .catch((error) => console.error("Ошибка при сохранении данных:", error));
  }, [data]);

  const handleDateChange = useCallback((e, itemId) => {
    const { value } = e.target;

    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, date: value } : item
    );
    setData(updatedData);

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        date: value,
      })
      .catch((error) => console.error("Ошибка при обновлении даты:", error));
  }, [data]);

  const handleCheckboxChange = useCallback(async (e, itemId) => {
    const { checked } = e.target;
    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, isTrue: checked } : item
    );
    setData(updatedData);

    try {
      await axios.put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        isTrue: checked,
      });
    } catch (error) {
      console.error("Ошибка при обновлении данных:", error);
    }
  }, [data]);

  const handleAddNewDriver = async () => {
    if (newDriver.trim() === "") return;

    try {
      const response = await axios.post("http://localhost:5000/api/drivers", {
        name: newDriver,
      });
      setDrivers([...drivers, response.data]);
      setNewDriver("");
    } catch (error) {
      console.error("Ошибка при добавлении водителя:", error);
    }
  };

  const groupByMonthAndDay = (data) => {
    return data.reduce((groups, item) => {
      const date = new Date(item.date);
      const monthYear = date.toLocaleString("default", { month: "long" });
      const day = date.getDate();

      if (!groups[monthYear]) groups[monthYear] = {};
      if (!groups[monthYear][day]) groups[monthYear][day] = [];

      groups[monthYear][day].push(item);
      return groups;
    }, {});
  };

  const isToday = (itemDate) => {
    const today = new Date();
    const itemDay = new Date(itemDate);
    return (
      today.getDate() === itemDay.getDate() &&
      today.getMonth() === itemDay.getMonth() &&
      today.getFullYear() === itemDay.getFullYear()
    );
  };

  const getMonthName = (date) => {
    return new Intl.DateTimeFormat('ru-RU', { month: 'long' }).format(date);
  };

  return (
    <div>
      <div className="header">
        <h1>Таблица данных</h1>
        <div className="buttons">
          <button onClick={addEntriesForSelectedDate}>
            Добавить 37 записей на выбранную дату
          </button>
          <button onClick={addNewItem}>Добавить новый элемент</button>
          <button onClick={() => addNewItemWithClass("highlight")}>
            Добавить новый элемент с выделением
          </button>
          <button onClick={handleLogout}>Выйти</button>
        </div>
      </div>
      <div className="controls">
        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
        />
        <input
          type="text"
          value={newDriver}
          onChange={(e) => setNewDriver(e.target.value)}
          placeholder="Новый водитель"
        />
        <button onClick={handleAddNewDriver}>Добавить нового водителя</button>
      </div>
      <div className="data">
        {Object.entries(groupByMonthAndDay(sortedData)).map(([month, days]) => (
          <div key={month}>
            <h2>{replaceSoftSign(month)}</h2>
            {Object.entries(days).map(([day, items]) => (
              <div key={day} ref={isToday(items[0].date) ? currentDayRef : null}>
                <h3 className={isToday(items[0].date) ? 'today-class' : ''}>
                  {day} {replaceSoftSign(getMonthName(new Date(items[0].date)))}
                </h3>
                <table>
                  <thead>
                    <tr>
                      <th>Дата</th>
                      <th>Бренд</th>
                      <th>Водитель</th>
                      <th>Клиент</th>
                      <th>Номер маршрута</th>
                      <th>Часы</th>
                      <th>Номер автомобиля</th>
                      <th>Цена</th>
                      <th>Выбрано</th>
                      <th>Действия</th>
                    </tr>
                  </thead>
                  <tbody>
                    {items.map((item) => (
                      <tr key={item._id} className={item.colorClass}>
                        <td>
                          <input
                            type="date"
                            value={item.date}
                            onChange={(e) => handleDateChange(e, item._id)}
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.brand}
                            onChange={(e) => handleInputChange(e, item._id, 'brand')}
                          />
                        </td>
                        <td>
                          <select
                            value={item.driver}
                            onChange={(e) => handleInputChange(e, item._id, 'driver')}
                          >
                            <option value="">Выберите водителя</option>
                            {drivers.map((driver) => (
                              <option key={driver._id} value={driver.name}>
                                {driver.name}
                              </option>
                            ))}
                          </select>
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.customer}
                            onChange={(e) => handleInputChange(e, item._id, 'customer')}
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.routeNumber}
                            onChange={(e) => handleInputChange(e, item._id, 'routeNumber')}
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.hours}
                            onChange={(e) => handleInputChange(e, item._id, 'hours')}
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.vehicleNumber}
                            onChange={(e) => handleInputChange(e, item._id, 'vehicleNumber')}
                          />
                        </td>
                        <td>
                          <input
                            type="text"
                            value={item.price}
                            onChange={(e) => handleInputChange(e, item._id, 'price')}
                          />
                        </td>
                        <td>
                          <input
                            type="checkbox"
                            checked={item.isTrue}
                            onChange={(e) => handleCheckboxChange(e, item._id)}
                          />
                        </td>
                        <td>
                          <button onClick={() => handleDeleteWithConfirmation(item._id)}>
                            Удалить
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
